	function stickyHeader() {
		//DOC vars
		var scrolled = $(window).scrollTop();
		var channelLoc = $("#gb-channel-list-outer").position();
		var loc = channelLoc.top+$("#gb-channel-list-outer").outerHeight();
		var pad = $("#gb-channel-list-outer").outerHeight();

		if(scrolled >= loc && scrollLock == 0) {
			$("#gb-channel-list-outer").addClass("fixed-header");
			$("body").css("padding", pad+"px 0 0 0");
			scrollLock = 1;
		} else if(scrolled < loc && scrollLock == 1){
			$("#gb-channel-list-outer").removeClass("fixed-header");
			$("body").css("padding", "0");
			scrollLock = 0;
		}

        if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
            if($(".wcm-controls").hasClass("wcm-stuck")) {
                $(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
            }
        }
	}

	function pgScreenSize(){
		getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			//photoGallery(650, 350, 1024);
			logoWidth();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			//photoGallery(467, 250, 768);
			logoWidth();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			//photoGallery(467, 250, 640);
			logoWidth();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			//photoGallery(480, 256, 480);
			appView();
			logoWidth480();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			//photoGallery(650, 350, 320);
			appView();
			logoWidth480();
		}
	}

	function photoGallery() {
    	var imageWidth, imageHeight, breakPoint;
        var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			imageWidth = 650;
            imageHeight = 350;
            breakPoint = 1024;
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			imageWidth = 467;
            imageHeight = 250;
            breakPoint = 768;
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			imageWidth = 467;
            imageHeight = 250;
            breakPoint = 640;
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			imageWidth = 480;
            imageHeight = 256;
            breakPoint = 480;
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			imageWidth = 650;
            imageHeight = 350;
            breakPoint = 320;
		}

    	var showPlayPause = 'true';
        if(showPlayPause == 'true') {
            showPlayPause = 'yes';
        } else {
            showPlayPause = 'no';
        }

        var clickablePhoto;
        if(false) {
            clickablePhoto = "['image']";
        } else {
            clickablePhoto = "[]";
        }

        if($("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
            if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
                // PHOTO GALLERY ROTATOR
                $("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
                        "imageWidth" 			: imageWidth,
                        "imageHeight"			: imageHeight,
                        "efficientLoad"         : "no",
                        "appHeader"				: "no",
                        "titles"				: "yes",
                        "captions"				: "yes",
                        "playPause"             : showPlayPause,
                        "showTextPerPhoto"		: "yes",
                        "photoMoreLink" 		: "yes",
                        "photoMoreLinkText" 	: '',
                        "mainControls"			: "yes",
                        "links"					: "yes",
                        "bullets"				: "yes",
                        "hoverPause"			: "yes",
                        "transitionType"		: 'slide',
                        "transitionSpeed"		: 1,
                        "allLoaded"				: function() {
                            $(".rs-photo-gallery-control.play-pause").prependTo(".rs-photo-gallery-bullets");
                            //DOC add ADA text for more-link
                            $("#hp-slideshow .rs-photo-gallery-photo-more-link a").prepend('Read More');
                            //DOC move rotator bullets
                            $("#hp-slideshow .rs-photo-gallery-bullets-outer").appendTo($("#hp-slideshow .rs-photo-gallery-container"));
                            //DOC replace rotator button arrows
                            $("#hp-slideshow .rs-photo-gallery-control").addClass("transition-all animated");
                            $("#hp-slideshow .rs-photo-gallery-control.back").html('');
                            $("#hp-slideshow .rs-photo-gallery-control.next").html('');
                            //DOC show controls on hover 
                            $("#hp-slideshow").hover(function() {
                                $("#hp-slideshow .rs-photo-gallery-control").removeClass("fadeOutLeft fadeOutRight");
                                $("#hp-slideshow .rs-photo-gallery-control.back").addClass("fadeInLeft").css("opacity", 1);
                                $("#hp-slideshow .rs-photo-gallery-control.next").addClass("fadeInRight").css("opacity", 1);
                            }, function() {
                                $("#hp-slideshow .rs-photo-gallery-control").removeClass("fadeInLeft fadeInRight");
                                $("#hp-slideshow .rs-photo-gallery-control.back").addClass("fadeOutLeft").css("opacity", 0);
                                $("#hp-slideshow .rs-photo-gallery-control.next").addClass("fadeOutRight").css("opacity", 0);
                            });
                            if(breakPoint >= 768) {
                                $("#hp-slideshow .rs-photo-gallery-photo-desc").hover(function() {
                                    $(".rs-photo-gallery-photo-caption", this).slideToggle(250);
                                }, function() {
                                    $(".rs-photo-gallery-photo-caption", this).slideToggle(250);
                                });
                            }
                        }
                });
            }
        } else if($("#sw-content-container10.ui-hp div.ui-widget.app.multimedia-gallery").length) {
        	$("#hp-slideshow").addClass("using-mmg-app");
        	var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "efficientLoad" : true,
                "imageWidth" : imageWidth,
                "imageHeight" : imageHeight,
                "mobileDescriptionContainer": [], // [960, 768, 640, 480, 320]
                "galleryOverlay" : false,
                "linkedElement" : clickablePhoto,  // ["image", "title", "overlay"]
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : true,
                "thumbnails" : false,
                "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "slide",
                "transitionSpeed" : 1,
                "transitionDelay" : 7,
                "fullScreenRotator" : false,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                "onTransitionEnd" : function(props) {

                }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) {
                	$("#hp-slideshow .mmg-control").addClass("transition-all animated");
                    $("#hp-slideshow .mmg-control.back").html('');
                    $("#hp-slideshow .mmg-control.next").html('');
                    $("#hp-slideshow-outer").hover(function() {
                        $("#hp-slideshow .mmg-control").removeClass("fadeOutLeft fadeOutRight");
                        $("#hp-slideshow .mmg-control.back").addClass("fadeInLeft").css("opacity", 1);
                        $("#hp-slideshow .mmg-control.next").addClass("fadeInRight").css("opacity", 1);
                    }, function() {
                        $("#hp-slideshow .mmg-control").removeClass("fadeInLeft fadeInRight");
                        $("#hp-slideshow .mmg-control.back").addClass("fadeOutLeft").css("opacity", 0);
                        $("#hp-slideshow .mmg-control.next").addClass("fadeOutRight").css("opacity", 0);
                    });
                    if(breakPoint >= 768) {
                        $("#hp-slideshow .mmg-description").hover(function() {
                            $(".mmg-description-caption", this).slideToggle(250);
                        }, function() {
                            $(".mmg-description-caption", this).slideToggle(250);
                        });
                    }

                    if(!$("#hp-slideshow .mmg-bullets .mmg-control.play-pause").length) {
                    	$("#hp-slideshow .mmg-control.play-pause").prependTo("#hp-slideshow .mmg-bullets");
                    }

                    var slideshowContWidth = $("#hp-slideshow-outer").width();
                    var bulletsPos = (slideshowContWidth / 2);
                    $("#hp-slideshow .mmg-bullets-outer").css({
                    	"width": slideshowContWidth + "px",
                        "margin-left": "-" + bulletsPos + "px"
                    });

                    $("#hp-slideshow .mmg-slide").each(function() {
                    	var thisSlide = this;
                        if(!$(".mmg-description-title",thisSlide).length) {
                        	if($(".mmg-description-caption",thisSlide).length) {
                            	$(".mmg-description",thisSlide).addClass("no-title");
                                $(".mmg-description",thisSlide).off();
                            }
                        }
                        if($(".mmg-description .mmg-description-links",thisSlide).length) {
                        	$(thisSlide).find(".mmg-description-links").prependTo(thisSlide);
                        }
                    });

                    $("#hp-slideshow .mmg-description-link").wrapInner("");

                }, // props.element, props.mmgRecords
                "onWindowResize": function(props) {
                	var slideshowContWidth = $("#hp-slideshow-outer").width();
                    var bulletsPos = (slideshowContWidth / 2);
                    $("#hp-slideshow .mmg-bullets-outer").css({
                    	"width": slideshowContWidth + "px",
                        "margin-left": "-" + bulletsPos + "px"
                    });
                } // props.element, props.mmgRecords
            });
        } else {
        	// PHOTO GALLERY PLACEHOLDER
            if(!$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").length) {
                $("#sw-content-container10.ui-hp").html("


var photoGallery0000={'Album':{'title': 'Photo Gallery','description': '','transitiondelay': '6','fadestyle': 'NO','gallerywidth': '" + imageWidth + "','backgroundaudio': '','loopaudio': 'False','lgpath': '','tnpath': '','Images': [{'photoname': 'Photography Showcase','src': '/cms/lib/NY19000908/Centricity/Template/7/placeholders/rotating_images1.jpg','caption': '','pause': '','link': '','target': ''},{'photoname': 'Photography Showcase Two','src': '/cms/lib/NY19000908/Centricity/Template/7/placeholders/rotating_images2.jpg','caption': '','pause': '','link': '','target': ''},{'photoname': 'Photography Showcase Three','src': '/cms/lib/NY19000908/Centricity/Template/7/placeholders/rotating_images3.jpg','caption': '','pause': '','link': '','target': ''}]}}</" + "scr" + "ipt>");
            }
        }
	}

	function logoWidth() {
		if($("#gb-logo-container img").length) {
			var imgWidth = $("#gb-logo img").width();
			$("#gb-logo-outer").width(imgWidth);
		}
	}

	function logoWidth480() {
		if($("#gb-logo-container img").length) {
			var imgWidth = $("#gb-logo img").width()*.75;
			$("#gb-logo-outer, #gb-logo-container img").width(imgWidth);
		}
	}

	function appView() {
		//DOC dont add app into accordion if it doesnt have a header
		$(".hp div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});
		$("div.ui-hp div.ui-widget-header").off('click').click(function(){
			if($(this).parent().hasClass("active")) {
				$("div.ui-widget.app").removeClass("active");
			} else {
				var clickedApp = $(this).parent().parent().attr("id");

				$("div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			}
		});
	}		

