        } else {
             var callback = "$('#calendar-pnl-categoryfilter-open').fadeOut('fast',function() { $('#calendar-pnl-categoryfilter-closed').fadeIn('fast');});";
             GetContent("https://www.ntschools.org//site/UserControls/Calendar/CategoryFilterListWrapper.aspx?ModuleInstanceID=" + "1223" + "&Expanded=" + false, "calendar-pnl-categoryfilter-closed", 3, callback); 
         }
     }

    function OpenCategoryFilter() {
