    function channelHeaderImage(){
        if($(".sp").length){
            //PLACE CHANNEL IMAGES INTO AN ARRAY

            var bgImage = '';
            var channelBgImages = ['','/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg'];
			var defaultImage = "/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///images/default-sp-header.jpg";

            var activeChannelText = $(".sw-channel-item.active span").text();

            if(!$(".sw-channel-item.active").length || activeChannelText == "Home"){
            	$("header").css("background-image", "url('"+defaultImage+"')");
            } else {
            	//FIND THE ORDER OF THE ACTIVE CHANNEL IN THE CHANNEL BAR
                var activeChannelOrder = $(".sw-channel-item.active").index();



                bgImage = channelBgImages[activeChannelOrder];

                //CHECK FOR CALENDAR PAGE
                if(activeChannelText == 'Calendar'){
                    $("header").css("background-image", "url('"+defaultImage+"')");
                } else {
                     //CHECK TO SEE IF THERE IS AN IMAGE FOR THE ACTIVE CHANNEL
                    if (bgImage.indexOf("default-man") < 0){
                        $("header").css("background-image", "url('"+bgImage+"')");

                    } else {
                        if (defaultImage.indexOf("default-man") < 0){
                            $("header").css("background-image", "url('"+defaultImage+"')");
                        }
                    }
                }
            } 
        }
    }
