/* ------------------------------   DYNAMIC CONTENT  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */      
	function dynamicContent() {

		// ********** MYSTART ********** //
		$(".ac-dropdown-container .selector").on('click keypress', function(event){
	        if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            controlDropdowns(event);
	        }
	    });

		//GLOBALLY CLOSE DROPDOWNS WHEN HOVERED AWAY FROM
	    $(".ac-dropdown-container").hover(function(e) {
			e.preventDefault();
		}, function() {

			$(".ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
			$(this).removeClass("open"); 
		});

		//SHOW USER OPTIONS IF THEY ARE LOGGED IN
		if($("#sw-mystart-account").length){
        	$(".ac-dropdown-container.user-options .ac-dropdown-list li.user.logged").show();
        	$(".ac-dropdown-container.user-options .ac-dropdown-list li").not(".ac-dropdown-container.user-options .ac-dropdown-list li.logged").remove();
        } else {
        	 //HIDE REGISTER BUTTON IF HIDDEN IN SITE MANAGER
              if(!$("#ui-btn-register").length){
                  $(".ac-dropdown-container .ac-dropdown-list li.user.signin").show();
              } else {
              	$(".ac-dropdown-container .ac-dropdown-list li.user.signin").show();
              	$(".ac-dropdown-container .ac-dropdown-list li.user.register").show();
              }

              $(".ac-dropdown-container.user-options .ac-dropdown-list li.logged").remove();
        }


		// ********** HEADER ********** //

		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/NY02205427/Centricity/Template/GlobalAssets/images///logos/rye-city-logo.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

		// ADD SITENAME
		var siteNameOne = jQuery.trim('Rye City');
		var siteNameTwo = jQuery.trim('School District');

		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Rye City School District";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		// ********** CHANNEL BAR ********** //
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //

		//WRAP FIRST WORD IN ALL APP HEADERS
		$(".app").not(".sp.column.one .app").each(function(){
			var rawAppTitle = $("div.ui-widget-header h1", this).text();
			rawAppTitle = rawAppTitle.split(" ");

			if(rawAppTitle.length > 1){
				var newAppTitle = "";
				for(i = 0; i < rawAppTitle.length; i++){
					if(i == rawAppTitle.length-1){
						newAppTitle += ""+rawAppTitle[i]+"";
					} else {
						newAppTitle += rawAppTitle[i]+" ";
					}

				}
				$("div.ui-widget-header h1", this).html(newAppTitle);
			}

		});

		//OUR SCHOOLS LINKS
		$(".school").on('click keypress', function(event){
	        if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            var schoolLink = $(this).attr("data");

	            window.open(schoolLink, '_self');
	        }
	    });

        //PHOTO ELEMENT HOVERS
		$(".school").focus(function() {
			$(this).find(".school-contact-info-container").attr("aria-hidden","false");
		});

		$(".school").focusout(function() {
			$(this).find(".school-contact-info-container").attr("aria-hidden","true");
		});

		//WHY CHOOSE HOVERS
		$(".school").hover(function() {
			$(this).find(".school-contact-info-container").attr("aria-hidden","false");
		}, function() {
			$(this).find(".school-contact-info-container").attr("aria-hidden","true");
		});

		//PLACE THE HEADLINES READ MORE BUTTON INTO THE APP HEADER, AND ADJUST RSS BUTTON
		$(".headlines").each(function(){
			//$("a.more-link", this).prependTo(".ui-widget-footer", this).show();
            var moreLink = $(this).find("a.more-link");

            if(moreLink.length > 0){
            	var moreLinkURL = $(this).find("a.more-link").attr("href");
				$(this).find(".ui-widget-header h1").append("VIEW ALL HEADLINES");
				$(this).find(".ui-widget-detail").append("VIEW ALL HEADLINES");
            }

			$(this).find(".rss span").text("subscribe to our feed");
		});

		//FIX FIRST BREADCRUMB
		$(".ui-breadcrumb-first span").text("Home").show();

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
    	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
    		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
    		//ADD SIGNED OUT BREADCRUMB
    		$(".ui-breadcrumbs").append("
Signed Out");
    	}

    	//ADD SIGN IN BREADCRUMB
    	if($("#swlogin").length){
    		$("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
    		$(".ui-breadcrumbs").append("
Sign In");
    	}

    	//REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}	  

        // MAKE NAVIGATION APPS ACCESSIBLE
        head.ready("accessibleNavApp", function(){
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        });

		// ********** FOOTER ********** //
		// MOVE SCHOOLWIRES FOOTER STUFF
		$(".gb-bb-footer.logo").html($("#sw-footer-logo").html());
		$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(0) a"));
		$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-bb-footer.links").append($("#sw-footer-copyright"));		

		// FOOTER LINKS
		$("#to-top").click(function(e){
			e.preventDefault();
            		$("html, body").animate({ scrollTop: 0 }, "slow", function() {
            			$("li.sw-channel-item:first-child > a").focus();
            		});
		});
	}  

	function controlDropdowns(event){
		var dropdownToOpen = $(event.currentTarget).parent().find(".ac-dropdown-list").attr("id");
		if($("#"+dropdownToOpen).parent().parent().hasClass("open")){
			$("#"+dropdownToOpen).parent().attr("aria-hidden", "true").slideUp(300, "swing"); 
			$("#"+dropdownToOpen).parent().parent().removeClass("open");
			$("#"+dropdownToOpen).parent().prev().attr("aria-expanded","false");

		} else {
			$("#"+dropdownToOpen).parent().attr("aria-hidden","false").slideDown(300, "swing", function(){
				setTimeout(function(){
					$("#"+dropdownToOpen).find("li:first a").focus();
				}, 100);
			});
			$("#"+dropdownToOpen).parent().parent().addClass("open");
			$("#"+dropdownToOpen).parent().prev().attr("aria-expanded","true");
		}
	}
