	function mmGallery() {
        if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {                
            var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));                
            mmg.props.defaultGallery = false;

            $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "efficientLoad" : true,
                "imageWidth" : 1500,
                "imageHeight" : 720,
                "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                "galleryOverlay" : false,
                "linkedElement" : [], // ["image", "title", "overlay"]
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : false,
                "thumbnails" : false,
                "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "fade", // fade, slide, custom
                "transitionSpeed" : 3,
                "transitionDelay" : 3,
                "fullScreenRotator" : false,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onTransitionStart" : function(props) {
                    $(".mmg-description").animateAuto("both", 1000);
                },  // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                "onTransitionEnd" : function(props) {
                },  // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) {
                    //MOD CONTROLS
                    $(".mmg-control", props.element).wrapAll('
');
                    $(".mmg-control.next", props.element).prependTo($(".mmg-controls", props.element));
                    $(".mmg-controls", props.element).insertBefore($(".mmg-slides-outer", props.element));
                    $(".mmg-controls", props.element).prependTo($(".mmg-description-outer", props.element));

                    //MOD DESCRIPTION 
                    $(".mmg-description").animateAuto("both", 0);
                }, // props.element, props.mmgRecords
                "onWindowResize": function(props) {} // props.element, props.mmgRecords
            });
        }
    }

	function mmRotator(){
		head.ready("multiMediaRotator", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
		        // MULTIMEDIA ROTATOR
		        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
		                "imageWidth"              : 1500,
		                "imageHeight"             : 720,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : [""], // ["image", "title", "overlay", "all"]
		                "playPause"               : "yes",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "More",
		                "descMoreLink"            : "yes",
		                "descMoreLinkText"        : "READ MORE",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["768"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 5, //changed from 8 by Lyndsay - Case 02911788 - 9/12/17
		                "customResize" : "no",
						"customResizeFunction" : function(element, imageWidth, imageHeight) {
							var screenWidth = $(window).width();
							if(screenWidth <= 768){
								imageWidth = 768;
								imageHeight = 389;								
							} 

							// RESET THE WIDTHS, HEIGHTS, AND SLIDE POSITION MAINLY IN CASE THE USER IS DRAGGING THE CORNER OF THEIR BROWSER
							// UPDATE WIDTH AND HEIGHT VARIABLES
							// SET viewWidth VARIABLE WITH A MAX-WIDTH OF config.imageWidth
							var viewWidth = ($(element).width() > imageWidth) ? imageWidth : $(element).width();
							var viewRatio = imageHeight / imageWidth;
							var viewHeight = viewWidth * viewRatio;
							var slideNum = $(".rs-gallery-images-list li", element).length;
							var sliderWidth = viewWidth * slideNum;
							if($(".rs-gallery-videos").length) {
								var vidWidth = $(".rs-gallery-videos .video", element).width();
								var vidHeight = $(".rs-gallery-videos .video", element).height() - 27;
							}

							// UPDATE LEFT POSITION OF VIEWER SLIDE
							var slidePos = $(".rs-gallery-images-list li.active", element).index();
							viewWidth = parseInt(viewWidth);
							var newPos = slidePos * viewWidth;

							// SET THE CSS PROPERTIES
							$(".rs-gallery-container", element).css({"height": viewHeight + "px"});
							$(".rs-gallery-container .rs-gallery-images", element).css({"height": viewHeight + "px"});
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list", element).css({"width": sliderWidth + "px", "height": viewHeight + "px", "left": "-" + newPos + "px"});
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li", element).css({"width": viewWidth + "px", "height": viewHeight + "px"});
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li img", element).removeAttr("style");

						},
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { afterMMR(); }
		        });
			}
		});
	}

	function afterMMR(){
		$(".rs-gallery-control.play-pause").insertBefore(".rs-gallery-desc");	
        $("#hp-slideshow-outer").removeClass("min-height");

	}
