}	
	function sameHeight(){
		spMaxHeight = $("#sp-content-outer").height();
		$(".sp.column.one, .sp.column.two").height(spMaxHeight);
		// IF .children() OF SELECTOR RESIZES THE RESET HEIGHT OF SELECTOR
		$(".sp.column.one, .sp.column.two").children().resize(function()
		{	
			$(".sp.column.one, .sp.column.two").height("");
			spMaxHeight = $("#sp-content-outer").height();
			$(".sp.column.one, .sp.column.two").height(spMaxHeight);
		});
	}
function schoolLevel()
{
	// IF schoolTextFiles IS ON THEN ADD THE DYNAMIC TEXT FILE
	if(schoolTextFiles == "on")
	{
	$.ajax({
		url: "/cms/lib/NY01001491/Centricity/Template/9/setup/sewanhaka.txt",
		dataType: "script",
		success: function()
		{
			initialize();
		},
		error: function()
		{
			alert("An error has occured initializing setup files.");
		}
	});
	}
	else
	{
	initialize();
	}
}
function customLogo(){
	if(customLogos != "none") {
		// #gb-logo a img -- IF customLogos IS ON THEN ADD THE customLogo
		if(customLogos == "on") {
			$("#gb-logo a img").attr("src","/cms/lib/NY01001491/Centricity/Template/9/logos/sewanhaka.png");
		} else {
			$("#gb-logo a img").attr("src","/cms/lib/NY01001491/Centricity/Template/9/logos/_logo.png");
		}
		$("#gb-logo").show(); 
	}
}
function loadFlash(){
	// #hp-slideshow -- IF #hp-slideshow EXISTS THEN ADD THE SWFObject
	if($("#hp-slideshow").size()){
		var so = new SWFObject("/cms/lib/NY01001491/Centricity/Template/9/swf/SP-Overlay.swf","mymovie", "585", "320", "9", "#FFFFFF");
		so.addParam("wmode", "transparent");
		so.addVariable("flashColor", secondaryColor);
		so.write("hp-slideshow-overlay");
	}
}
function dynamicStylesheet(){
	// .append NEW STYLE SHEET TO HEAD OF THE PAGE TO STYLE CHANNEL BAR ITEMS
	var dynStyleSheet = document.createElement('style');
	if(dynStyleSheet) {
		dynStyleSheet.setAttribute('type', 'text/css');
		var head = document.getElementsByTagName('head')[0];
		if(head) {
      			head.appendChild(dynStyleSheet);
		}
		var dynStyles = 	"a, a:visited {" +
					"	color:" + linkColor + ";" +
					"	text-decoration:" + linkDecoration + ";" +
					"}" +
					"ul.sw-channel-list li.sw-channel-item > a {" +
					"	color: " + channelTextColor + ";" +
					"}" +
					"ul.sw-channel-list li.sw-channel-item.active > a, ul.sw-channel-list li.sw-channel-item.hover > a, ul.sw-channel-list li.sw-channel-item > a:hover {" +
					"	background-color: " + channelBarColorActive + ";" +
					"	color: " + channelTextColorActive + ";" +
					"}" +
					"ul.sw-channel-dropdown li a {" +
					"	color: " + channelDropdownTextColor + ";" +
					"}" +
					"ul.sw-channel-dropdown li a:hover {" +
					"	background-color: " + channelDropdownColorHover + ";" +
					"	color: " + channelDropdownTextColorHover + ";" +
					"}";
		var rules = document.createTextNode(dynStyles);
		if(dynStyleSheet.styleSheet){// IE
			dynStyleSheet.styleSheet.cssText = dynStyles;
		} else {
			dynStyleSheet.appendChild(rules);
		}
	}
}
function dynamicContent()
{
	$("#sw-channel-list-container").append('
');
	//DOC .sw-mystart-button.home -- SETS DISTRICT BUTTON ON/OFF
	if(districtButton == "off")
	{
	$(".sw-mystart-button.home").hide();

	}
	// SETS BODY COLOR
	$("body").css("background-color",backgroundColor);

	// SETS CHANNEL BAR STYLES
	$("#gb-channel-list").css("background-color", channelBarColor);
	$("ul.sw-channel-dropdown").css("background-color", channelDropdownColor);
	$("ul.sw-channel-dropdown").css("border-color", backgroundColor);
	//**DROPDOWN TEXT COLOR AND TEXT HOVER STATE IS SET IN THE DYNAMIC STYLESHEET FUNCTION**

	// SETS COLUMN COLORS
	$("#gb-header-outer, #hp-column-one-header,.hp.column.one,.sp.column.one").css("background-color",primaryColor);
	$("ul.ui-breadcrumbs li > a").css("color",primaryColor);

	//DOC #sw-content-container1 .ui-widget-header h1 -- SETS REGION BODY AND HEADER COLORS
	$(".ui-hp#sw-content-container1 .ui-widget-header h1").css({"color": region1HeaderColor, "border-color": region1HeaderColor});
	$(".ui-hp#sw-content-container1").css("color",region1BodyColor);
	$(".ui-hp#sw-content-container1 a, .ui-hp#sw-content-container1 > a > span").css("color",region1LinkColor);
	//DOC #sw-content-container2 .ui-widget-header h1 -- SETS REGION BODY AND HEADER COLORS
	$(".ui-hp#sw-content-container2 .ui-widget-header h1").css({"color": region2HeaderColor, "border-color": region2HeaderColor});
