	function templateLibrary(){
		// SHOWS ONLY THE TEMPLATES THATS IN THE SAME CATEGORY AS THE TEMPLATE CURRENTLY VIEWED
        	if(window.location.host == "templatelibrary.schoolwires.net") {
        		var parentAlias = "sewanhaka";
			if(parentAlias == "silver" || parentAlias == "bronze"){
				$.ajax({
				  url: "/cms/lib2/SW00000032/Centricity/Domain/4/scripts/templateDropdown.js",
				  dataType: "script",
				  success: function() {
				  	modifyDropdown(parentAlias);
				  },
				  error: function() {
				  	alert("An error has occured initializing dropdown modification.");
				  }
				});
			}
		}
	}

