body.script:
jQuery(function($) {



        $("#mobnav > a").click(function(event) {
          event.preventDefault();
        });


        var windowSize = $(window).width();
        if(windowSize < 768) {


          $('.pf-cssmenu-mobilenav > .sub > a').one("click", function(event) {
            event.preventDefault();
            $(this).parent().toggleClass('item-on');
            $(this).parent().children('ul').slideDown('fast');
        }
    );
  }
});
