	function jsQueries(){
		var getWidth = jsBreakPoint();
		if (getWidth == 960) { // 960
			districtTabbedFeature();
		} if (getWidth == 768) { // 768
			districtTabbedFeature();
		} if (getWidth == 640) { // 640
			districtTabbedFeature();
		} if (getWidth == 480) { // 480
			districtAccordionFeature();
			appView();
		} if (getWidth == 320) { // 320
			districtAccordionFeature();
			appView();
		}
	}

	function mediaRotator(){
		head.ready("mediaRotator", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
				// MULTIMEDIA ROTATOR
				$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
					"imageWidth"              : 590,
					"imageHeight"             : 335,
					"appHeader"               : "no",
					"titles"                  : "yes",
					"captions"                : "yes",
					"playPause"               : "no",
					"mainControls"            : "no",
					"contMoreLink"            : "no",
					"contMoreLinkText"        : "Read More",
					"descMoreLink"            : "yes",
					"descMoreLinkText"        : "MORE",
					"bullets"                 : "no",
					"bulletControls"          : "no",
			 		"thumbnails"              : "no",
					"thumbnailWidth"          : 122,
					"thumbnailHeight"         : 53,
					"thumbnailSlideDirection" : "horizontal", // horizontal or vertical
					"hoverPause"              : "yes",
			                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
					"fullScreenVideo"         : "yes",
					"transitionType"          : "fade", // fade or slide
					"transitionSpeed"         : 2,
					"transitionDelay"         : 6,
					"onImageLoad"             : function() { },
					"allImagesLoaded"         : function() { },
					"onThumbnailLoad"         : function() { },
					"allThumbnailsLoaded"     : function() { },
					"onTransitionStart"       : function() { },
					"onTransitionEnd"         : function() { },
					"watchVidClick"           : function() { },
					"closeVidClick"           : function() { },
					"allLoaded"               : function() {
						$("#hp-slideshow .rs-gallery-desc-more-link").prepend('');
						slideshowMoreInit();
						slideshowMoreBtn();
					}
				});
			}
		});
	}

	function slideshowMoreBtn() {
		if($(".hp").length && $("#hp-slideshow .rs-gallery-desc-more-link").length) {
			var descH = $("#hp-slideshow .rs-gallery-desc").outerHeight(true);
			$("#hp-slideshow .rs-gallery-desc-more-link").css("height", descH + "px");
		}
	}

	function globalIconTxtCenter() {
		$(".gb-global-icons .global-icon").each(function() {
			var iconW = $(this).width();
			var iconTxtW = $(".text", this).width();
			var iconPos = (iconW - iconTxtW) / 2;
			$(".text", this).css("left", iconPos + "px");
		});
	}

	function appView() {
		//DOC dont add app into accordion if it doesnt have a header
		$(".hp-content.bottom .ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
		$(".hp-content.bottom div.ui-widget-header").unbind("click");
		$(".hp-content.bottom div.ui-widget-header").click(function(){
			if($(this).parent().hasClass("cs-active")) {
				$(this).parent().removeClass("cs-active");
			} else {
				var clickedApp = $(this).parent().parent().attr("id");

				$(".hp-content.bottom div.ui-widget.app").removeClass("cs-active");
				$(this).parent().removeClass("cs-active").addClass("cs-active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			}
		});
	}

	function useChannelForSection() {
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent() {
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron, .sw-dropdown-arrow").text("");

		//DOC move mystart buttons and dropdowns
		$(".gb-mystart.right").append($(".sw-mystart-button.home"));

			if($(".sw-mystart-button.register").length) {
				$(".gb-mystart.left").prepend($(".sw-mystart-button.register"));
			}
			$(".sw-mystart-button.signin a span").text("LOG IN");
			$(".gb-mystart.left").prepend($(".sw-mystart-button.signin"));


		// SET SPANISH TRANSLATE LINK
		$(".sw-mystart-button.translate a").attr("href", "//translate.google.com/translate?u=" + window.location.href + "&langpair=en|es&hl=en&anno=0&ie=UTF8");


		// ********** HEADER ********** //
		// AJAX IN THE SCHOOL LIST
		// CHECK IF IT IS SUPPORTED BY THE USERS BROWSER
		if(typeof(Storage) !== "undefined"){ // IS SUPPORTED
			if(sessionStorage.getItem("lincolnSchoolList") === null) {
				// ITEM DOES NOT EXIST IN STORAGE
				$("#gb-school-list-outer").load("/cms/lib/NC01911169/Centricity/Template/17/html/school-menu.min.html", function(){
					// BIND THE MENU EVENTS
					schoolListEvents();

					// STORE SCHOOL LIST
					// JSON STRINGIFY IS USED BECAUSE SOME JAVASCRIPT MAY BE STORED
					var stuffToStore = $("#gb-school-list-outer").html();
					sessionStorage.setItem("lincolnSchoolList", JSON.stringify(stuffToStore));
				});
			} else {
				// ITEM DOES EXIST IN STORAGE
				// JSON PARSE IS USED BECAUSE OF THE CONTAINED JAVASCRIPT
				$("#gb-school-list-outer").html(JSON.parse(sessionStorage.getItem("lincolnSchoolList")));

				// BIND THE MENU EVENTS
				schoolListEvents();
			}
		} else { // IS NOT SUPPORTED
			// BROWSER DOES NOT SUPPORT HTML5 STORAGE
			$("#gb-school-list-outer").load("/cms/lib/NC01911169/Centricity/Template/17/html/school-menu.min.html", function(){
				// BIND THE MENU EVENTS
				schoolListEvents();
			});
		}

		//DOC add logo
		var logoSrc = jQuery.trim("/cms/lib/NC01911169/Centricity/Template/GlobalAssets/images///Logos/lincoln-logo.png");
		if("true" == "true" && $(".hp").length) {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").html('').removeClass("hidden");
			}
			$(".gb-header.left").addClass("has-logo");
		} else {
			$(".gb-header.left").addClass("no-logo");
		}

		//DOC add sitename
		if("false" == "true" && $(".hp").length) {
			var siteNameOne = jQuery.trim('');
			var siteNameTwo = jQuery.trim('EDUCATING THE FUTURE');
			if((siteNameOne == "") && (siteNameTwo == "")) {
				var splitLen = 2;
				var siteName = "Lincoln County Schools";
				siteName = siteName.split(" ");
				var siteNameLength = siteName.length;
				if(siteNameLength > 2){
					siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
				} else {
					siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
				}
				siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
				$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
			} else if((siteNameOne != "") && (siteNameTwo == "")) {
				$("#gb-sitename").prepend("
" + siteNameOne + "");
			} else if((siteNameOne == "") && (siteNameTwo != "")) {
				$("#gb-sitename").prepend("
" + siteNameTwo + "");
			} else if((siteNameOne != "") && (siteNameTwo != "")) {
				$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
			}

			$("#gb-sitename").removeClass("hidden");
			$(".gb-header.left").addClass("has-sitename");
		} else {
			$(".gb-header.left").addClass("no-sitename");
		}

		//DOC tagline
		if("true" == "true" && $(".hp").length) {
			$("#gb-tagline").removeClass("hidden");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		//DOC set icon for home channel
		var firstChannelText = $("li.sw-channel-item:first > a span").text().toLowerCase();
		if(firstChannelText == "home") {
			$("li.sw-channel-item:first > a span").addClass("sw-icon").text("h");
			$("li.sw-channel-item").first().addClass("home");
		}

		// CHANNEL SEARCH BUTTON
		$("#gb-channel-search-btn").click(function(e) {
			e.preventDefault();

			if($(this).hasClass("cs-active")) {
				$("#gb-channel-search-btn").removeClass("cs-active");
				$("#gb-search-bottom-outer").slideUp();
			} else {
				$("#gb-channel-search-btn").addClass("cs-active");
				$("#gb-search-bottom-outer").slideDown();
			}
		});

		//MOBILE SEARCH BUTTON
		$("#gb-mobile-search-btn").click(function(e) {
			e.preventDefault();

			if($(this).hasClass("cs-active")) {
				$("#gb-mobile-search-btn").removeClass("cs-active");
				$("#gb-search-top").slideUp();
			} else {
				$("#gb-mobile-search-btn").addClass("cs-active");
				$("#gb-search-top").slideDown();
			}
		});

		// ********** BODY ********** //
		//DOC replace more-link text
		$("a.more-link span").text("View All");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").addClass("sw-icon").text("h");
		//DOC register breadcrumb
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$(".ui-breadcrumbs").append("
Register");
		}
        	//DOC add read full story button to each healine
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
        			$(this).append('More');
        		});
        	});
        	// APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        	$("div.ui-widget.app .ui-widget-detail img")
        		.not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
        		.not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
        		.each(function() {
        			if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
					$(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
				}
		});
		// HOMEPAGE ANNOUNCEMENTS ALERT CHECK
		if($("#hp-alert .ui-widget.app.announcements li").length) {
			$("#hp-alert-outer").removeClass("hidden");
		}

		//Added by Paul - Case #00108248;
 		$(".hp-content.top .ui-widget.app.headlines .ui-article").each(function(){
        	if($("img", this).attr("alt") != undefined){ //-- Added by Chris Case 00261235
              if($("img", this).attr("alt").indexOf("www") > -1 || $("img", this).attr("alt").indexOf("http") > -1) {
                  $(".custom-read-more", this).attr("href", $("img", this).attr("alt"));
