	function schoolListEvents(){
		// SCHOOL LIST BUTTON
		$("#gb-select-school").click(function(e) {
			e.preventDefault();

			if($(this).hasClass("cs-active")) {
				$("#gb-school-list-outer").slideUp(function() {
					$("#gb-select-school").removeClass("cs-active");
				});
			} else {
				$("#gb-school-list-outer").slideDown(function() {
					$("#gb-select-school").addClass("cs-active");
				});
			}
		});

		// TAB HEADER
		$(".gb-school-list-header").click(function(e){
			e.preventDefault();
			// IF THE SELECTED TAB ISNT ALREADY ACTIVE
			if(!$(this).hasClass("active")){
				// GET CLASS OF SELECTED TAB
				var selectedList = $(this).attr("class");
				selectedList = jQuery.trim(selectedList.replace("gb-school-list-header ", ""));

				//REMOVE CURRENT ACTIVE CLASSES
				$(".gb-school-list-header.active, .gb-school-list-section.active").removeClass("active");

				// SET NEW ACTIVE CLASSES
				$(this).addClass("active");
				$(".gb-school-list-section." + selectedList).addClass("active");
			}
		});
	}

	function districtTabbedFeature() {
		// DISTRICT HOMEPAGE TAB ROTATOR
		if("lcsnc" == csDistrict && $(".hp-content.top .headlines").length) {
			$(".hp-content.top .ui-widget.app.headlines .ui-widget-header").unbind("click");

			// SET UP SOME VARIABLES
			var rotateTheFeatures = "true";

			// FEATURE HOVER
			$(".hp-content.top").hover(function() {
				// STOP THE TIMER IF IT IS TURNED ON
				if(rotateTheFeatures) { stopFeatureTimer(); }
				$(this).addClass("features-paused");
			}, function() {
				// RESTART THE TIME IF IT IS TURNED ON
				if(rotateTheFeatures) { stopFeatureTimer(); runFeatureTimer(); }
				$(this).removeClass("features-paused");
			});

			if($(".hp-content.top").hasClass("loaded")){
				// ROTATE THE FEATURES IF TURNED ON AND NOT HOVERING
				stopFeatureTimer();
				runFeatureTimer();
			} else {
				// REMOVE ALL APPS EXCEPT FOR HEADLINES
				$(".hp-content.top .ui-widget.app").not($(".hp-content.top .ui-widget.app.headlines")).parent().parent().parent().remove();

				$(".hp-content.top .headlines").each(function(i) {
					// ADD THE APP INDEX
					$(this).attr("index", i);

					// GET THE APP HEADER FOR THE TAB
					$(".hp-feature-rotator.tabs").append('' + $.trim($(".ui-widget-header", this).text()) + '');

					// REMOVE ALL RECORDS OVER THREE
					$(".ui-articles li:gt(2)", this).remove();
				});

				// SET THE FIRST HEADLINES APP TO ACTIVE
				$(".hp-feature-rotator.tabs .hp-feature-tab").first().addClass("cs-active");
				$(".hp-feature-rotator.content .headlines").first().addClass("cs-active");

				// SHOW THE TABBED AREA
				$(".hp-content.top").removeClass("hidden");

				// TAB CLICK
				$(".hp-feature-tab").click(function(e) {
					e.preventDefault();

					// GET NEW ACTIVE INDEX
					var tabIndex = $(this).attr("index");

					// SET TAB ACTIVE STATE
					$(".hp-feature-tab").removeClass("cs-active");
					$(this).addClass("cs-active");

					// SET APP ACTIVE STATE
					$(".hp-feature-rotator.content .headlines.cs-active").removeClass("cs-active");
					$(".hp-feature-rotator.content .headlines[index='" + tabIndex +"']").addClass("cs-active");
				});

				// ROTATE THE FEATURES IF TURNED ON AND NOT HOVERING
				runFeatureTimer();

				$(".hp-content.top").addClass("loaded");
			}
		}
	}

	function runFeatureTimer() {
		if("true" == "true" && !$(".hp-content.top").hasClass("features-paused")) {
			featureRotateTimer = setInterval(function() {
				rotateFeatures();
			}, 4 * 1000);
			csFeatureTimer = true;
		}
	}

	function stopFeatureTimer() {
		if(csFeatureTimer) {
			clearInterval(featureRotateTimer);
			csFeatureTimer = false;
		}
	}

	function rotateFeatures() {
		// SET TAB ACTIVE STATE
		var nextActiveTab = $(".hp-feature-tab.cs-active").next(".hp-feature-tab");
		$(".hp-feature-tab.cs-active").removeClass("cs-active");
		if(nextActiveTab.length) {
			nextActiveTab.addClass("cs-active");
		} else {
			$(".hp-feature-rotator.tabs .hp-feature-tab").first().addClass("cs-active");
		}

		// SET APP ACTIVE STATE
		$(".hp-feature-rotator.content .headlines.cs-active").removeClass("cs-active");
		$(".hp-feature-rotator.content .headlines[index='" + $(".hp-feature-tab.cs-active").attr("index") +"']").addClass("cs-active");
	}

	function districtAccordionFeature() {
		// DISTRICT HOMEPAGE TAB ROTATOR
		if("lcsnc" == csDistrict && $(".hp-content.top .headlines").length) {
			// REMOVE ALL APPS EXCEPT FOR HEADLINES
			$(".hp-content.top .ui-widget.app").not($(".hp-content.top .ui-widget.app.headlines")).parent().parent().parent().remove();

			// UNBIND EVENTS
			stopFeatureTimer();
			$(".hp-content.top").unbind("hover");

			// APP HEADER CLICK
			$(".hp-content.top .ui-widget.app.headlines .ui-widget-header").unbind("click");
			$(".hp-content.top .ui-widget.app.headlines .ui-widget-header").click(function(){
				if($(this).parent().hasClass("m-active")) {
					$(this).parent().removeClass("m-active");
				} else {
					var clickedApp = $(this).parent().parent().attr("id");

					$(".hp-content.top .ui-widget.app.headlines").removeClass("m-active");
					$(this).parent().addClass("m-active");

					$("html, body").animate({
						scrollTop: $("#" + clickedApp).offset().top
					}, 500, function(){
						$("html, body").stop(true, false);
					});
				}
			});

			$(".hp-content.top").removeClass("hidden");
		}
	}

	function buildStyleSheet(){
		var headerFooterColor = "rgba(" + hexToRgb('#5E5E6D') + ", 0.80)";

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles = '#gb-header-outer {' +
						'background: url(/cms/lib/NC01911169/Centricity/Template/17/gb-noise-texture.png) repeat left top #5E5E6D;' +
						'background: url(/cms/lib/NC01911169/Centricity/Template/17/gb-noise-texture.png) repeat left top ' + headerFooterColor + ';' +	
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function modEvents() {
		head.ready("modEvents", function() {
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "no"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}

			// REARRANGE TITLES AND TIMES
			$(".ui-widget.app.upcomingevents .ui-article-description").each(function() {
				$(this).prepend($(".sw-calendar-block-title", this));
			});
		});
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			}
		});
	}

	function tabbedBlock() {
		head.ready("tabbedBlock", function() {
			$("#sw-content-container5.ui-hp").tabBlocks();

			if($("#sw-content-container5.ui-hp .upcomingevents").length) {
				$("#sw-content-container5.ui-hp .upcomingevents").removeAttr("style");
			}
		});
	}

	function searchText() {
		$(".gb-search-button").click(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-form").submit(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-input").focus(function() {
		    if($(this).val() == "Search") {
		        $(this).val("");
		    }
		});
		$(".gb-search-input").blur(function() {
		    if($(this).val() == "") {
		        $(this).val("Search");
		    }
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		var getWidth = jsBreakPoint();
		if(getWidth >= 640){ // 640+
			$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
		} if(getWidth < 640){ // 480-
			$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
			$(".ui-btn-toggle.month").removeClass("active");
			$(".ui-btn-toggle.list").addClass("active");
		}
	}

	function jsBreakPoint(){
		// 960
		if ($(window).width() >= 1024) { return 960; } 

		// 768
		if ($(window).width() >= 768 && $(window).width() < 1024) { return 768; }

		// 640
		if ($(window).width() >= 640 && $(window).width() < 768) { return 640; }

		// 480
		if ($(window).width() >= 480 && $(window).width() < 640) { return 480; }

		// 320
		if ($(window).width() >= 320 && $(window).width() < 480) { return 320; }
	}

	function slideshowMoreInit() {
		var canvas = document.getElementById("rs-gallery-more-bg");

		//IF IE
		if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
		var ctx = canvas.getContext("2d");

		slideshowMoreDraw(ctx);
	}

	function slideshowMoreDraw(ctx) {
		ctx.save();
		ctx.beginPath();
		ctx.moveTo(0.0, 74.8);
		ctx.lineTo(89.0, 74.8);
		ctx.lineTo(97.9, 0.0);
		ctx.lineTo(0.0, 0.0);
		ctx.lineTo(8.3, 39.0);
		ctx.lineTo(0.0, 74.8);
