        	$("div.ui-widget.app.gallery.json").creativeGalleryRotate({
                "efficientLoad" : "yes",
                "imageWidth" : 320,
                "imageHeight" : 220,
                "appHeader" : "yes",
                "titles" : "no",
                "captions" : "no",
                "playPause" : "no",
                "mainControls" : "yes",
