	                        break;
	                    case 3:
	                        dayClass = 'fc-wed';
	                        break;
	                    case 4:
	                        dayClass = 'fc-thu';
	                        break;
	                    case 5:
	                        dayClass = 'fc-fri';
	                        break;
	                    case 6:
	                        dayClass = 'fc-sat';
	                        break;
	                }

	                $('td.' + dayClass + '.' + dateClass).addClass('fc-active-day').attr('m', calEvent.start.getMonth()).attr('d', calEvent.start.getDate()).attr('y', calEvent.start.getFullYear());
