                        $("#hp-slideshow .mmg-controls-cont.main-gallery").append('
');
                    }
                }, // props.element, props.mmgRecords
                "onWindowResize": function(props) {
                	$(".mmg-description", props.element).each(function() {
                                if(!$(".mmg-description-inner",this).length) {
