                            }
                        });

                        if($(".cs-fullscreen-video-link").length) {
                        	$(".cs-fullscreen-video-link > a").wrapInner("");
                        }
                    },
                    "onStateChange" : function() { }
                });

                $(".cs-fullscreen-video-control-text").text("");

                // ADD DESCRIPTION
                var showVideoTitle = true;
                var showVideoCaption = true;
                var showVideoLink = true;
