        "Search": function() {
        	// FOR SCOPE
            var _this = this;

            $(document).on("click keydown", "#gb-search-toggle", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true");

                        $("#gb-search-form").addClass("show-search").attr("aria-hidden", "false");
                        $("#gb-search-input").attr({
                        	"aria-hidden": "false",
                            "tabindex": "0"
                        }).focus();
                    } else {
                        $(this).attr("aria-expanded", "false");

                        $("#gb-search-form").removeClass("show-search").attr("aria-hidden", "true");
                        $("#gb-search-input").attr({
                        	"aria-hidden": "true",
                            "tabindex": "-1"
                        });
                    }
                }
            });

            $(document).on("keyup", function() {
            	if($("#gb-search-form").attr("aria-hidden") == "false" && !$("#gb-search-form").find(":focus").length && !$("#gb-search-toggle").is(":focus")) {
                	$("#gb-search-toggle").attr("aria-expanded", "false");
                    $("#gb-search-form").removeClass("show-search").attr("aria-hidden", "true");
                    $("#gb-search-input").attr({
                        	"aria-hidden": "true",
                            "tabindex": "-1"
                        });
                }
            });

            $("#gb-search-input").focusout(function() {
            	setTimeout(function() {
                    if(!$("#gb-search-input").find(":focus").length && !$("#gb-search-toggle").is(":focus") && !$("#gb-search-button").is(":focus")) {
                        $("#gb-search-toggle").attr("aria-expanded", "false");
                        $("#gb-search-form").attr("aria-hidden", "true").removeClass("show-search");
                    }
                }, 500);
            }).keydown(function(e) {
            	if(e.keyCode == _this.KeyCodes.esc) {
                	e.preventDefault();

                    $("#gb-search-toggle").attr("aria-expanded", "false");
                    $("#gb-search-form").attr("aria-hidden", "true").removeClass("show-search");
                }
            });

			$("#gb-search-form").submit(function(e){
				e.preventDefault();

				if($("#gb-search-input").val() != "Search...") {
                	if($("#gb-search-input").val() != "") {
						window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                    }
                }
			});

			$("#gb-search-input").focus(function() {
				if($(this).val() == "Search...") {
					$(this).val("");
				}
			}).blur(function() {
				if($(this).val() == "") {
					$(this).val("Search...");
				}
			});
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "hexToRgb": function(h) {
        	// FOR SCOPE
            var _this = this;

        	var r = parseInt((_this.cutHex(h)).substring(0,2),16);
	        var g = parseInt((_this.cutHex(h)).substring(2,4),16);
	        var b = parseInt((_this.cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },

        "cutHex": function(h) {
        	return (h.charAt(0)=="#") ? h.substring(1,7):h;
        }
    };

