    function SLM(){
    	$.schoolListMinibase({
            'miniBaseDomainID': 1,
            'miniBaseModuleInstanceID': 63452,
            'cache': true,
            'schoolListReady': function(schoolList) {
                /*
                The returned value is an array of objects and each object is a school record.
                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                */
                schoolList.unshift({
                	'schoolAlias': 'councilrocksd',
                    'schoolName': 'District Home',
                    'schoolType': 'Alternative',
                    'schoolUrl': '/councilrocksd',
                    'schoolVisible': 'TRUE'
                });

                var schools = '';

                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        schools += '
' + schoolList[i].schoolName + '';
                    }
                }

                //$('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);

                if( $('.sw-channel-item.schools-channel').length > 0 ){
                	$('.sw-channel-item.schools-channel').append('' + schools + '');
                    $('.sw-channel-item.schools-channel a').attr('aria-haspopup', 'true');
                }

                $("#gb-channel-list .sw-channel-item.schools-channel a").keydown(function(e) {
                    var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                    var linkObj = $('#gb-channel-list .sw-channel-item.hover a');
                    // CAPTURE KEY CODE
                    switch(e.keyCode) {
                        // CONSUME LEFT AND UP ARROWS
                        case csKeyCodes.left:
                        case csKeyCodes.up:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS FIRST ITEM
                            if(linkObj.index($(this)) == 0) {
                                $('.sw-channel-item.hover > a').focus();
                            } else {
                                linkObj[linkObj.index($(this)) - 1].focus();
                                //$(this).parent().prev("li").find("> a").focus();
                            }
                        break;

                        // CONSUME RIGHT AND DOWN ARROWS
                        case csKeyCodes.right:
                        case csKeyCodes.down:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS LAST ITEM
                            if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                                // FOCUS FIRST ITEM
                                linkObj[0].focus();
                            } else {
                                // FOCUS NEXT ITEM
                                linkObj[linkObj.index($(this)) + 1].focus();
                            }
                        break;
                    }
                });

                rsMenu(schoolList);
            }
        });
    }

    function rsMenu(schoolList){
    	//'1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
        var mobileSchools = '';

        for(var i in schoolList) {
            if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                mobileSchools += '1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
            }
        }

        var extraMenuOptions = '';

		var extraMenuItems = '';

		var globalIcons = {
        	"": ["#", "_self"],
			"": ["#", "_self"],
            "": ["#", "_self"],
            "": ["#", "_self"],
            "": ["#", "_self"],
            "": ["#", "_self"]
		};

		$.each(globalIcons, function(k, v){
			if( k != "" ){
				extraMenuItems += '2, ' + k + ', ' + v[0] + ', ' + v[1] + ';';
			}
		});

		if( extraMenuItems != "" ) extraMenuOptions = 'Global Links';


    	// RESPONSIVE MAIN MENU
		$("body").csRsMenu({
		    "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
			"primaryColor"          : "#5a5a5a", // DEFAULT - #2C363E
			"secondaryColor"        : "#252525", // DEFAULT - #222A30
			"accentColor"           : "#FFF", // DEFAULT - #B5BBBF
			"menuButtonParent"      : "#gb-channel-list",
			"showMenuBtnText"       : "no",
			"menuBtnText"           : "MENU",
			"showSchools"           : "no",
			"showTranslate"         : "yes",
			"translateId"           : "none",
			"showAccount"           : "no",
			"extraMenuOptions"      : "Schools;" + extraMenuOptions, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"        : mobileSchools + extraMenuItems, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"                : "1"
		});
    }

	function pgScreenSize(){
		var getWidth = $(window).width();
		multimediaRotator();

		if (getWidth >= 1024) { //Desktop View
            if( $('.hp').length > 0 ){
            	sameHeights('#hp-content .column');
                if( $('#hp-content .column.two').hasClass('moved-for-mobile') ){
                	$('#hp-content .column.one').after($('#hp-content .column.two'));
                    $('#hp-content .column.two').removeClass('moved-for-mobile');
                }
            }
            console.log('pg screensize sameheights');
            sameHeights('#sp-content > .column');
		}else{ //Mobile BPs
        	if( $('.hp').length > 0 ){
        		$('#hp-content .column').css('min-height',  '');

                if( !$('#hp-content .column.two').hasClass('moved-for-mobile') ){
                	$('#hp-content .column.one').before($('#hp-content .column.two'));
                    $('#hp-content .column.two').addClass('moved-for-mobile');
                }

                if( getWidth < 640 ){
                	appView();
                }
            }
            $('#sp-content > .column').css('min-height', '');
		}
	}

    function sameHeights(columnSelector){
    	var maxHeight = 0;
        $(columnSelector).css('min-height',  '');
        $(columnSelector).each(function(k, v){
            if( $(this).outerHeight() > maxHeight ) maxHeight = $(this).outerHeight();
        });
        console.log(maxHeight);
        $(columnSelector).css('min-height', maxHeight);
    }

