	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {

            $(".upcomingevents .ui-article").each(function(k, v){
            	var dateSplit = $('.sw-calendar-block-date', this).text().split(',');
				var year = new Date().getFullYear();
                if(dateSplit.length > 1){
                	year = dateSplit[1].trim();
                }

            	$(this).attr('data-year', year);
            });

	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "no",
	            dayWeek     : "yes"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
        $("li", container).each(function(){
            if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                var prevDate = $(this).prev().find("h1.ui-article-title.sw-calendar-block-date").html();

              $('.ui-article', this).addClass('upcoming-column-container ui-clear').prepend('

' + prevDate + '');
                $('.ui-article-description', this).wrap('
');
            };
        });

        $(".ui-article", container).each(function(articleIndex, article){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex] != undefined && newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ' '; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

            //if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){
				var weekDay = newDateTime[0].split('>');
                $('.upcoming-column.right', this).prepend(weekDay[0] + ">" + weekDay[1] + "");
			}

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	

			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).append('ALL DAY');
				}

                $('.sw-calendar-block-title', this).after($('.sw-calendar-block-time', this));
			});
		});
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if( $('.ui-widget-header h1 .accordion-handle', this).length <= 0){
					$('.ui-widget-header h1', this).append('');
				}
			}
		});

		//DOC accordion header click
		$("#hp-content div.ui-widget-header").off('click').click(function(){
            if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
                $(this).parent().removeClass("active");
            }else{
                var clickedApp = $(this).parent().parent().attr("id");
                $("#hp-content div.ui-widget.app.active").removeClass("active"); 
                $(this).parent().addClass("active");
                smoothScroll("#" + clickedApp, 10);
            }
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
Family Resources");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		if($(".sw-mystart-button.signin").length > 0){
			$('#custom-mystart-signin').append($(".sw-mystart-button.signin").html());
			$('#custom-mystart-signin a').append('').addClass("mystart-label");
            $('#custom-mystart-signin a span').addClass('hover-text');

			if($(".sw-mystart-button.register").length > 0){ 
				$('#custom-mystart-register').append($(".sw-mystart-button.register").html());
                $('#custom-mystart-register a').addClass("mystart-label");
                $('#custom-mystart-register a span').addClass('hover-text');
			}
		}
		if($('.sw-mystart-button.manage').length > 0){
			$('#gb-user-options ul').append("
" + $('.sw-mystart-button.manage').html() + "");
		}
		if($('#sw-mystart-mypasskey').length > 0){
			$('#sw-mystart-mypasskey').removeClass('sw-mystart-button').prependTo("#gb-mystart-outer");
            $('#ui-btn-mypasskey').addClass('mystart-label');
            $('#ui-btn-mypasskey span').html(' Passkeys');
		}
		$('#sw-myaccount-list li').appendTo('#gb-user-options ul');

		$('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#gb-school-dropdown ul');

		// ********** HEADER ********** //
        //DOC add logo
		var logoSrc = jQuery.trim("/cms/lib/PA01000188/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg"); //element set
		var srcSplit = logoSrc.split("/");
		var srcSplitLen = srcSplit.length;
		if( "true" == "true" ){
            if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                $("#header-school-logo img").attr('src', logoSrc);
            }else{
                $("#header-school-logo img").attr('src', defaultHeaderLogoSrc);
            }
        }else{
        	$("#header-school-logo img").remove();
        }

        globalIcons();

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

        $("li.sw-channel-item").each(function(channelIndex, channel){
        	if( $('> a span', channel).text().toLowerCase() == "schools" ){
            	$(channel).addClass('schools-channel');
            	return true
            }
        });

		//Overwrite system hover functionality for an animated version.
		$('#channel-navigation > .sw-channel-item > a').off('touchstart');
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){ //in
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(200, "swing");
			}
			$(this).addClass("hover");
		}, function(){ //out
			$(".sw-channel-dropdown").slideUp(200, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More ");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //DOC add signed out breadcrumb
            $(".ui-breadcrumbs").append("
Signed Out");
        }
        //DOC add sign in breadcrumb
        if($("#swlogin").length){
            $(".ui-breadcrumbs").append("
Sign In");
        }
        //DOC add read full story button to each headline
        $(".ui-widget.app.headlines").each(function(){
            $(".ui-article", this).each(function(){
                var headlineURL = $("h1 a", this).attr("href");
                $(".ui-article-description", this).append('Read More ');
            });
        });

        $('.ui-widget.app').each(function(k, v){
        	if( $('.more-link', v).length > 0 ){
            	$('.ui-widget-footer', v).prepend($('.more-link', v));
                $('.more-link', v).addClass('big-button-style').html('
View All ');
                $('.more-link-under', v).remove();
            }

            if( $('.view-calendar-link', v).length > 0 ){
            	$('.view-calendar-link', v).prependTo($('.ui-widget-footer', v));
                $('.view-calendar-link', v).addClass('big-button-style').html('
View All Events ');
            }

            $('.ui-widget-footer .clear', v).remove();
            if( $('.ui-widget-footer', v).children().length <= 0 ){
            	$('.ui-widget-footer', v).hide();
            }
        });

        $('.hp .ui-widget.app.navigation a').wrapInner(function(item){
            if($('> img', this).length <= 0){
            	$(this).html('' + $(this).html() + '');
            }else{
            	$(this).parent().addClass('image-link');
            }
        });
        $('.hp .ui-widget.app.navigation .site-shortcuts > li:not(.image-link) > a')
        	.addClass('big-button-style')
            .prepend('
')
            .append('');

        $('*[data-hide="test-"]').remove();

        modEvents();

		// ********** FOOTER ********** //

        var logoSrc = jQuery.trim("/cms/lib/PA01000188/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg"); //element set
		var srcSplit = logoSrc.split("/");
		var srcSplitLen = srcSplit.length;
		if( "true" == "true" ){
            if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                $("#school-logo img").attr('src', logoSrc);
            }else{
                $("#school-logo img").attr('src', defaultLogoSrc);
            }
        }else{
        	$("#school-logo img").remove();
        }

		//DOC get Schoolwires footer links
		$("a#feedback").text($("div#sw-footer-links li:eq(0) a").text()).attr("href", $("div#sw-footer-links li:eq(0) a").attr("href"));
		$("a#terms").text($("div#sw-footer-links li:eq(2) a").text()).attr("href", $("div#sw-footer-links li:eq(2) a").attr("href"));
		$("a#policy").text($("div#sw-footer-links li:eq(1) a").text()).attr("href", $("div#sw-footer-links li:eq(1) a").attr("href"));		
		$("p#copyright").text($('#sw-footer-copyright').text());

        $('div#bb-legal-logo').append( $('#sw-footer-logo').html() );

        $('#gb-mystart-outer > a:first').attr('id', 'sitemap-footer-link').addClass('mystart-item mystart-label');
        $('#gb-mystart-outer > a:first span').html(' Site Map');
	}

	function searchText() {
		var defSearchText = "Search...";

	    var searchBox = $('#swsitesearch-txt-searchstring');
        searchBox
			.attr("aria-label", "Search input")
			.attr("placeholder", '')
			.val(defSearchText)
			.focus(function() {
                if($(this).val() == defSearchText) {
                    $(this).val("");
                }

                /* If the search trigger isn't to the right of the search input. 
                searchBox.off('keyup keydown keypress').on('keydown', function(e) { // Trigger the click event from the keyboard
                    var code = e.which;
                    // 13 = Return, 32 = Space
                    if ((code === 9) ) {
                        e.preventDefault();
                        $('#gb-search-trigger').focus();
                    }
                });
                */
            })
            .blur(function() {
                if($(this).val() == "") {
                    $(this).val(defSearchText);
                }

                //searchBox.off('keydown keyup keypress');
            });

            $('#search-trigger').attr('onclick', $('#search-container input[type="button"]').attr('onclick'));
	}

