	$(window).load(function(){

		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		searchText();

		var headerHeight = $("#gb-header-outer").height();
		//$("#gb-logo, #gb-sitename").height(headerHeight);

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.schoollist").unbind("hover");
		$(".sw-mystart-dropdown.schoollist").hover(function(){

		},function(){
			$(".sw-dropdown-arrow",this).removeClass("rotate-arrow");
			$(this).removeClass("open");
			$("#cs-schoollist-dropdown-list").slideUp("slow", "swing");
		});


		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.translate").unbind('hover');
		$(".sw-mystart-dropdown.translate").hover(function(e){

		}, function(e) {
			e.stopPropagation();
			$(".sw-dropdown-arrow").removeClass("rotate-arrow");
			$("#cs-translate-dropdown-list").slideUp("slow", "swing");
			$(this).removeClass("open");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$("#sw-mystart-account").unbind('hover');
		$("#sw-mystart-account").hover(function(){

		}, function() {
			$(this).removeClass("open");
            $(".sw-dropdown-arrow", this).removeClass("rotate-arrow");
			$("#cs-sw-myaccount-list").slideUp("slow", "swing");
		});

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-item ul").stop(true, true);
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

        //HIDE EMPTY DROPDOWN THAT GETS PLACED BY THE SYSTEM IF CHANNEL HAS NO SECTIONS BUT CHANNEL HOMEPAGE IS TURNED ON
		$("ul.sw-channel-dropdown").each(function() {
			var systemDropdown = $(this);
			if(!$("li",systemDropdown).length) {
				$(systemDropdown).remove();
			}
		});

	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ 
			pgScreenSize(); 
			if($(window).width() >= 320 && $(window).width() <= 639){
				calendarListView();
			}
            checkSlideshow();
		});

        $(window).scroll(function() { checkSlideshow(); });

        checkSlideshow();

		var translator = 'true';
		if (translator == "false"){
			$(".sw-mystart-dropdown.translate").hide();
			var useTranslate = "no";
            $("#sw-mystart-inner .sw-mystart-dropdown.schoollist").css("border","none");
		}
		else {
			addFramesetTranslate();
			var useTranslate = "yes";
		}

		var districtHome = 'true';
		if (districtHome == "false"){
			$(".sw-mystart-button.home").hide();
		}
		else {
			$(".sw-mystart-button.home").show();
		}

		if($(".sw-mystart-dropdown.schoollist").length) {
			var useSchoollist = "yes";
		} else {
			var useSchoollist = "no";
		}

		var additionalHeaders = "", additionalLinks = "";

		// SET UP GLOBAL ICONS FOR RS MENU
		var menuGlobalIcons = [
			["PowerSchool", "https://powerschool.methacton.org/public/home.html", "People/6.png"],
			["My MSD", "http://www2.methacton.org/", "Communication/19.png"],
			["Contact Us", "https://www.methacton.org/Page/41", "Communication/21.png"],
			["Calendar", "https://www.methacton.org/Page/2", "Site Utility/2.png"],
			["Staff Email", "https://mail.methacton.org/", "Communication/18.png"]
		];
		var menuIconNum = ("4" == "5;4;3;2;1;0") ? 4 : "4";

		for(var i = 0; i < menuIconNum; i++) {
			var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
            var gbIcon = "";
			if($.trim(menuGlobalIcons[i][2]).indexOf("cms") > -1) {
				gbIcon = "";
			} else {
				var splitSelection = menuGlobalIcons[i][2].split("/");
				var iconCat = $.trim(splitSelection[0]);
				iconCat = iconCat.toLowerCase().replace(/\s+/g, "-");
				var iconChoice = splitSelection[1].split(".");
				iconChoice = iconChoice[0];
				var iconSelection = iconCat + "-" + iconChoice;
				gbIcon = "";
			}
			additionalLinks += "1, " + gbIcon + " " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
		}

		if(menuIconNum > 0) {
			additionalHeaders = "Popular Links";
		}

		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"	: "left-to-right", // OPTIONS - left-to-right, right-to-left
			"menuButtonParent"	: "#gb-channel-list",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "Menu",
			"showSchools"		: useSchoollist,
			"showTranslate"		: useTranslate,
			"translateId"		: "none",
			"showAccount"		: "yes",
			"extraMenuOptions"	: additionalHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"		: additionalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"			: "1",
			"allLoaded"             : function(){}
		});

		$("body").csRsPagelistMenu({
		        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
		        "primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
