$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=NLUYFpIL%2bSh4vVtM0XpN2w%3d%3d&SessionID=c97aed22-f4f1-4126-a7de-e238d477de0d&SiteID=1&ChannelID=27&SectionID=1373&PageID=19388&HitDate=4%2f22%2f2020+11%3a58%3a03+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.1.1';
