function schoolName(){
	var siteID = "alvinisd";
	var topWords = "";

       	switch(siteID) {
       		case "edfoundation":
            case "longfellowes":	// Added by Lyndsay - Case #00306575 06-08-2016
       			topWords = "two"; //Added By Chris - Case 00185825
       		case "jeteres": 
       		case "masones": 
       		case "yorkes":
       		case "twainps":
       		case "twaines":
       		case "passmorees":
       		case "stevensonps":
       		case "savannahlakeses":
       		case "disneyes":
       		case "harbyjrhs":
       		case "ryanjrhs":
       		case "bond2013":
       		case "manveljrhs":
       		case "rpjh":
       		case "schs": //Added by Marie - Case 00173184
       			topWords = "two";
              	break;
              	case "wilderes":
              	case "marekes":
                case "brotherses": //added by Lyndsay - Case 00502091 - 3.7.17
                //case "mcnairjrhs": //added by Matt - Case #3104980 - 03.19.18
                case "sanchezes":
              		topWords = "three";
              	break;
                case "mcnairjrhs": //added by Matt - Case #3944537- 08.27.18
                case "nelsones": //Adam - 3.4.19 - #04098800
                case "redduke": //Adam - 3.4.19 - #04098800
              		topWords = "four";
                    break;
              	default:
              		topWords = "one";
       	}



	//SETUP A VARIABLE FROM THE HEADER TEXT
	var headerText = $("h1#gb-sitename").text();

	//SPLIT EACH WORD OF THE HEADER TEXT INTO AN ARRAY
	var headerArray = headerText.split(" ");

	if(topWords == "two"){
		//PRINT THE FIRST WORD OF THE APP HEADER BACK INTO THE H1
		$("h1#gb-sitename").html(headerArray[0] + " " + headerArray[1] + "
");		

		//FOR EACH ADDITIONAL WORD IN THE APP HEADER, WRAP IT IN A SPAN FOR CAPITALIZATION
		for(i = 2;i <= headerArray.length - 1;i++){
			$("h1#gb-sitename").append("" + headerArray[i] + "");
		}
	} else if(topWords == "three"){
		//PRINT THE FIRST WORD OF THE APP HEADER BACK INTO THE H1
		$("h1#gb-sitename").html(headerArray[0] + " " + headerArray[1]  + " " + headerArray[2] + "
");		

		//FOR EACH ADDITIONAL WORD IN THE APP HEADER, WRAP IT IN A SPAN FOR CAPITALIZATION
		for(i = 3;i <= headerArray.length - 1;i++){
			$("h1#gb-sitename").append("" + headerArray[i] + "");
		}
        if("alvinisd" == "sanchezes"){
        	$("h1#gb-sitename").css("font-size", "40px");
        } else {
        	$("h1#gb-sitename").css("font-size", "46px");
        }

    } else if(topWords == "four"){//added by Matt - Case #3944537- 08.27.18
		//PRINT THE FIRST WORD OF THE APP HEADER BACK INTO THE H1
		$("h1#gb-sitename").html(headerArray[0] + " " + headerArray[1]  + " " + headerArray[2]  + " " + headerArray[3]  +  "
");		

		//FOR EACH ADDITIONAL WORD IN THE APP HEADER, WRAP IT IN A SPAN FOR CAPITALIZATION
		for(i = 4;i <= headerArray.length - 1;i++){
			$("h1#gb-sitename").append("" + headerArray[i] + "");
		}
		$("h1#gb-sitename").css("font-size", "40px");  
	} else {
		//PRINT THE FIRST WORD OF THE APP HEADER BACK INTO THE H1
		$("h1#gb-sitename").html(headerArray[0] + "
");		

		//FOR EACH ADDITIONAL WORD IN THE APP HEADER, WRAP IT IN A SPAN FOR CAPITALIZATION
		for(i = 1;i <= headerArray.length - 1;i++){
			$("h1#gb-sitename").append("" + headerArray[i] + "");
		}
	}

	$("h1#gb-sitename").show();
}
function dynamicContent() {
	//ADD BB STUFF
    $(".footer.two").html($("#sw-footer-logo").html());
    $("#link-one").html($("#sw-footer-links li:eq(0) a"));
    $("#link-three").html($("#sw-footer-links li:eq(2) a"));
    $("#link-two").html($("#sw-footer-links li:eq(1) a"));
    $("#link-four").html($("#sw-footer-copyright"));

    //PLACE THE HEADLINES READ MORE BUTTON INTO THE APP HEADER
    $(".hp.column.two .headlines").each(function() {
        if($(".more-link", this).length){
            var moreLinkURL = $(this).find("a.more-link").attr("href");
            $(this).find(".ui-widget-header").append("
View All Headlines");
        }
    })


	//DISTRICT HOME BUTTON TEXT
	$("div.sw-mystart-button.home span").html("Alvin ISD
")

	//SETUP SEARCH
	$("div#sw-mystart-search").appendTo("div#gb-footer-search");
	$("a#sw-search-button img").attr("src", "/cms/lib/TX01001897/Centricity/Template/11/gb-search.png");
	$("div#sw-mystart-search").show();

	//CHANGE COLORS
	$("div#sw-mystart-outer, div#gb-header-outer, div.hp.column.one div.ui-widget-header, div.hp.column.two div.ui-widget-header, div.hp.column.three div.ui-widget-header, div.sp.column.one div.ui-widget-header, div#gb-footer-outer").css("background-color", primaryColor);
	$("div.sp.column.two div.ui-widget-header h1").css("color", primaryColor);
	$("div#gb-channel-list-outer, #gb-global-icons-outer").css("background-color", secondaryColor);
	$("div#gb-content-outer, a.tab, li.sw-channel-item.active > a, div#hp-content-footer, .rs-gallery-desc-more-link, #hp-content .divider").css("background-color", pageBG);
	$("a.tab, #gb-global-icons-outer, .hp.column.two div.ui-widget.app, div.sp.column.one").css("border-color", pageBG);
    $(".rs-gallery-desc-more-link a, .view-all a, .headlines .ui-article-title a, .upcomingevents h1.ui-article-title").css("color", accentColor);
    $("#mmr-control-wrapper .rs-gallery-control.play-pause").css("background-color", accentColor);


	//YOUTUBE BUTTON IN FOOTER
	if(youTubeButton == "yes"){
		$("a.ft-icon.youtube").show();
	}

	//ICON LINKS
	$("a.head-icon.contact").attr("href", contactURL);
	$("a.tab.students").attr("href", studentsURL);
	$("a.tab.parents").attr("href", parentsURL);
	$("a.tab.staff").attr("href", staffURL);
	$("a.ft-icon.facebook").attr("href", facebookURL);
	$("a.ft-icon.twitter").attr("href", twitterURL);
	$("a.ft-icon.tv").attr("href", tvURL);
	$("a.ft-icon.flickr").attr("href", flickrURL);
	$("a.ft-icon.youtube").attr("href", youtubeURL);
}
