        "ToggleSchools": function() {
            var _this = this;
        	$('#gb-header .school-btn').bind('click keydown', function(e) {
            		if(_this.AllyClick(e)) {
                  		e.preventDefault();

                    if($(this).attr('aria-expanded') == "false") {
                     	$('#gb-school-list').stop().slideDown(200);
                        $(this).attr('aria-expanded',true);
                        $(this).addClass('rotate');
                        $('#gb-school-list li:eq(0) a').focus();
                        $('#gb-school-list a').attr('tabindex', 0);



                     } 

                     else {
                     	$('#gb-school-list').stop().slideUp(200);
                        $(this).attr('aria-expanded',false);
                        $(this).removeClass('rotate');
                         $('#gb-school-list a').attr('tabindex', -1);
                     }

                  }

            });
        },

        "NavigateSchools":function() {
        	var _this = this;
            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            $('#gb-school-list a').keydown(function(e){
            	switch(e.keyCode) {

                	// CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                    	e.preventDefault();

                    if($(this).parent().index() == 0) {
                    	$('.school-btn').focus();


                    }else {
                    	$(this).parent().prev("li").find("a").focus()
                    }


                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                    if($(this).parent().is(":last-child")) {
                    	$(this).closest('ul').find("li:first-child > a").focus();


                    }else {
                    	$(this).parent().next("li").find("a").focus()
                    }

                    break;

                    // CONSUME ESC KEY
                    case csKeyCodes.tab:
                    case csKeyCodes.esc:
                     e.preventDefault();
                    	$('.school-btn').focus().attr('aria-expanded',false);
                        $('#gb-school-list').slideUp('fast');

                    break;
                }

            });

            $('.school-btn').keydown(function(e){
            		switch(e.keyCode) {

                    	//Up pressed
                    	case csKeyCodes.up:
                        e.preventDefault();

                        $('#gb-school-list li:last-child').find('> a').focus();

                        break;

                        //Down pressed
                        case csKeyCodes.down:
                        e.preventDefault();

                        $('#gb-school-list li:first-child').find('> a').focus();

                        break;

                    }

            });

            $('#gb-school-list').on('mouseleave', function() {
            	$('.school-btn').attr('aria-expanded',false);
            	$(this).slideUp('fast');

            });

            /*$('#gb-school-list').on('focusout', function() {

            	setTimeout(function () {
                    if(!$(this).find(":focus").length) {
                      $('.school-btn').attr('aria-expanded',false);
                      $('#gb-school-list').slideUp('fast'); 
                    }
                }, 1500);


            });*/
        },

