        "SiteShortcuts": function() {
        	var _this = this;
        	//TOGGLE WATERMARKS
			if(true) {
            	$('#primary').addClass('watermark');
            }

            if(true) {
            	$('#intermediate').addClass('watermark');
            }

            if(true) {
            	$('#middle').addClass('watermark');
            }

            //TOGGLE SHORTCUT INFO ON MENU OPEN/CLOSE

            //$('.hp .expandable ul a').attr('tabindex', '-1');
            $('.hp .expandable > a').attr('tabindex', '0').addClass('ss-btn').attr('role', 'button').attr('aria-expanded', 'false');



            $('.hp .site-shortcuts > li.expandable > a').on('click keydown', function(e) {
            var $this = this;


            	if(_this.AllyClick(e)) {
                	e.preventDefault();


                    if($(this).attr('aria-expanded') == true) {
                        	$(this).attr('aria-expanded', false);
                        }

                        else {
                        	$(this).attr('aria-expanded', true);
                        }

                    setTimeout(function() {
                    	$('.hp .expandable ul.showing a').attr('tabindex', '0');
                        $('+ ul.showing > li:first-child', $this).find('> a').focus();


                    }, 300);



                    if(_this.GetBreakPoint() == "desktop" || _this.GetBreakPoint() == "768"){

                        if($(this).next('ul').hasClass('showing')) {
                            $(this).next('ul').removeClass('showing');
                            $(this).parents().eq(7).find('.shortcut-info').fadeIn();
                        }


                        else {
                            $(this).next('ul').addClass('showing');
                            $(this).parents().eq(7).find('.shortcut-info').fadeOut();


                        }
                    }

                    if(_this.GetBreakPoint() == "640"){

                        if($(this).next('ul').hasClass('showing')) {
                            $(this).closest('.shortcut').animate({height: "124px"})
                            $(this).next('ul').removeClass('showing');



                        }

                        else {
                            $(this).closest('.shortcut').animate({height: "450px"})
                            $(this).next('ul').addClass('showing');

                        }

                    }

                    if( _this.GetBreakPoint() == "480" || _this.GetBreakPoint() == "320"){
                        if($(this).next('ul').hasClass('showing')) {
                            $(this).closest('.shortcut').animate({height: "193px"})
                            $(this).next('ul').removeClass('showing');



                        }

                        else {
                            $(this).closest('.shortcut').animate({height: "515px"})
                            $(this).next('ul').addClass('showing');

                        }
                    }

                    setTimeout(function() {
                    	$('.site-shortcuts ul').attr("aria-hidden", "true");
                    	$('.site-shortcuts ul.showing, .site-shortcuts ul.showing ul').attr("aria-hidden", "false");
                    }, 200);
                }


            });


            //NAVIGATE SITE SHORTCUTS
            var _this = this;
            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
			$('.hp .expandable > a').attr('tabindex', '0').addClass('ss-btn');	

            $("#hp-campus-shortcuts .siteshortcuts").each(function() {
                var link = $("ul.site-shortcuts a", this);
                var firstLinkIndex = $(link).index($(link).first());
                var firstLink = $(link).eq($(link).index($(link).first()));
                var lastLinkIndex = $(link).index($(link).last());
                var lastLink = $(link).eq($(link).index($(link).last()));

                $(link).keydown(function(e) {
                    var increment;
                    var thisIndex = link.index(this);
                    var newIndex = -1;

                    switch(e.keyCode) {
                        case _this.KeyCodes.up:
                        case _this.KeyCodes.left:
                            e.preventDefault();

                            if($(this).is(firstLink)) {
                                $(this).closest(".siteshortcuts").find("ul.site-shortcuts > li > ul > li:last-child > a").focus();
                            } else {
                                newIndex = thisIndex - 1;
                            }
                        break;

                        case _this.KeyCodes.down:
                        case _this.KeyCodes.right:
                            e.preventDefault();

                            if($(this).is(lastLink)) {
                                $(this).closest(".siteshortcuts").find(".site-shortcuts > li:first-child > a").focus();
                            } else {
                                newIndex = thisIndex + 1;
                            }
                        break;


                    }

                    if(newIndex != -1) {
                        link.eq(newIndex).focus();
                    }
                });
            });




        },


