        "MoveElements":function() {
        	var _this = this;
        	$('.shortcut').each(function() {
            	var isShowing = $(this).find('.showing').length;

                if(isShowing && _this.GetBreakPoint() == "desktop" || isShowing && _this.GetBreakPoint() == "768") {
                    $('.shortcut-info', this).hide();
                }
                else {
                	$('.shortcut-info', this).show();
                }
            });



            $('.hp .collapsible > a').each(function() {
            	var shortcutList = $(this).next('ul');


                    if($(shortcutList).hasClass('showing')) {
                          if(_this.GetBreakPoint() == "desktop"){
                              $(shortcutList).closest('.shortcut').css('height', '458px');
                          }

                          if(_this.GetBreakPoint() == "640"){
                              $(shortcutList).closest('.shortcut').css('height', '450px');
                          }

                          if(_this.GetBreakPoint() == "480" || _this.GetBreakPoint() == "320"){

                              $(shortcutList).closest('.shortcut').css('height', '515px');

                          }
                    }
            });




        	if (this.GetBreakPoint() == "desktop"){
        		//RESET DOM ELEMENTS ON DESKTOP
                $('#sw-mystart-mypasskey').insertBefore('.cs-mystart-dropdown.user-options');

            	$('#content-app1').insertBefore('#content-app2');

                //MOVE JERMEY DATE AFTER ADAM HUG ON MOBILE
                $('.hp .jeremy-date').each(function() {
                    var adamHug = $(this).next();
                    $(this).insertAfter(adamHug);
                });

                //MOVE FOOTER ELEMENTS
                $('.gb-footer-contact').prependTo('#gb-footer');
                if($('.btn-list').length == 0) {
                	$('').appendTo('.gb-footer-contact');
                    $('#footer-btns li:lt(2)').prependTo('.gb-footer-contact .btn-list');
                }

                if($('.btn-list').length == 1) {
                	$('').appendTo('#footer-links');
                    $('#footer-btns li').prependTo('#footer-links .btn-list');
                    $('#footer-btns').remove();
                }



            }

            else {
            	//MOVE DOM ELEMENTS ON MOBILE
                $('#sw-mystart-mypasskey').insertBefore('#rs-menu-btn');
                $('#content-app1').insertAfter('#content-app2');

                //MOVE JERMEY DATE BEFORE ADAM HUG ON MOBILE
                $('.hp .jeremy-date').each(function() {
                    var adamHug = $(this).prev();
                    $(this).insertBefore(adamHug);
                });

                //MOVE FOOTER ELEMENTS
                $('.gb-footer-logo').prependTo('#gb-footer');


                if($('#footer-btns').length == 0) {
            		$('').insertAfter('.gb-footer-contact');
                    $('.btn-list li').prependTo('#footer-btns');
                    $('.btn-list').remove();
                }

                $('#linkbank ul').addClass('flex');



            }

            if (this.GetBreakPoint() != "desktop"){
            	if($('#gb-footer-outer #top').length == 0) {
                	$('#top').prependTo('#gb-footer-outer');
                }
            }

            else {
            	if($('main #top').length == 0) {
                	$('#top').prependTo('main');
            	}

            }


        },


        "AppAccordion": function() {
            $("#hp-events, #hp-news, #fb-app, #more-info .col").csAppAccordion({
                "accordionBreakpoints" : [768, 640, 480, 320]
            });

            $('.sp.column.one').csAppAccordion({
