        "Search": function() {
        	var _this = this;
            //SEARCH TEXT
            $("#gb-search-input").removeAttr("placeholder").attr("value", "");

            $("#gb-search-input").focus(function() {
            	setTimeout(function() {
                  if($("#gb-search-input").val() == "Im looking for...") {
                      $("#gb-search-input").val("");
                  }
                }, 500);
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Im looking for...");
                }
            });

            //TOGGLE SEARCH
            $('#gb-search-btn').bind('click keydown', function(e) {
            		if(_this.AllyClick(e)) {
                  		e.preventDefault();

                        if($(this).attr('aria-expanded') == "false") {
            				$(this).attr('aria-expanded',true).addClass('color rotated');
                            $('#gb-search-input').addClass('open').attr('tabindex', 0);
                            $("#gb-search-input").val("");

                            setTimeout(function() {
                            	$('#gb-search-input').focus();
                            }, 500);
            			}
                        else {
                        	$(this).attr('aria-expanded',false).removeClass('color rotated');
                            $('#gb-search-input').removeClass('open').attr('tabindex', -1);
                            $("#gb-search-input").val("");
                        }
                    }

            });
        },

