                    var eventDate = $(this).parents('li').find(".jeremy-date").text();
					var eventMonth = $(this).parents('li').find(".joel-month").text();

                    var aLabel = eventDay + ", " +eventMonth+ " " +eventDate;  

                    if($(this).attr('aria-expanded') == "false") {
                        $(this).prev().find('.ui-article-description').slideDown();
                        $(this).attr('aria-expanded', true).addClass('open');
                        $(this).attr("aria-label","Show less events on " + aLabel);


                    }else {
                        $(this).attr('aria-expanded', false).removeClass('open');
                        $(this).prev().find('.ui-article-description:gt(2)').slideUp();
                        $(this).attr("aria-label","Show more events on " + aLabel);

                    }


                });


        },

