                $lightActive.attr("alt", "LightBox Active");
            }

            var $buttonMax = $(".rltbActionButton.rltbMaximizeButton");
            if ($buttonMax.length > 0) {
                $buttonMax.attr("title", "Maximize");
            }
        }
        Sys.Application.add_load(nse_onload);

