function adjustImgs1(){
    var imgTarget = $('.homeTabsNews-story .image > .inside > img');
    var imgTargetParent = $('.homeTabsNews-story .image > .inside');

    // Function check W:H of each image and adjusts accordingly
    imgTarget.each(function(){

        var imgW = $(this).width();
        var imgH = $(this).height();
        var imgWHr = imgW / imgH;

        var outW = imgTargetParent.width();
        var outH = imgTargetParent.height();
        var outWHr = outW / outH;

        if( outWHr >= imgWHr ){    
            $(this).css({
                'width' : '100%',
                'height' : 'auto'
            });
        }
        else {
            $(this).css({
                'width' : 'auto',
                'height' : '100%'
            });    
        }
    });
}

function adjustImgs2(){
    var imgTarget = $('.homeNews .image .inside img');
    var imgTargetParent = $('.homeNews .image .inside');

    // Function check W:H of each image and adjusts accordingly
    imgTarget.each(function(){

