        $(this).attr('data-tabNum', tabNum + 1);

    });

    $('ul.seisd-tabs').appendTo('#banner');

$('ul.seisd-tabs > li').prepend('');
$('ul.seisd-tabs > li').append('');

$('ul.seisd-tabs li').click(function(){
    var tabNum = $(this).attr('data-tabnum');
    
    $('ul.seisd-tabs > li').not($(this)).removeClass('active');
    $(this).addClass('active');
    $('ul.seisd-tabs').attr('data-border-color', tabNum);

    $('ul.seisd-tab-content > li.active').removeClass('active');
    $('ul.seisd-tab-content > li:not(.inactive)[data-tabnum="'+ tabNum +'"]').addClass('active');

});

});

