	$(document).ready(function() {
		//encode filepath as suggested by Google and set src attribute of iframe to that encoded url
        var filePath = "https://www.shermanisd.net//cms/lib/TX02215369/Centricity/Domain/24/3.20 SISD Distance Learning English Spanish.pdf";
        
        if (filePath !== ''){
        	$("#doc-viewer-text-16761").removeClass("ui-helper-hidden");
        }
		
        filePath = encodeURIComponent(filePath);
       
        var url = "https://docs.google.com/viewer?url=" + filePath + "&embedded=true";
        
        $("#doc-viewer-16761").attr("src", url);
	});

