        "SchoolMenuControls": function() {
        	// FOR SCOPE
            var _this = this;

            $(".gb-school-list-header-inner").off();

            $(document).on("click keypress", ".gb-school-list-header-inner", function(e) { 
                if(_this.AllyClick(e)) {
                	var index = $(this).parent().index();

					$(".gb-school-list-header, .gb-school-list-section").removeClass("active");
                    $(this).parent().addClass("active");
                    $(".gb-school-list-section").eq(index).addClass("active");
                }
            });

            $(document).on("keydown", ".gb-school-list-header-inner", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();

                        if($(this).parent().index() == "0") {
                        	$(".gb-school-list-header").last().find(".gb-school-list-header-inner").focus().click();
                        } else {
                        	$(this).parent().prev().find(".gb-school-list-header-inner").focus().click();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();

                        if($(this).parent().index() == $(".gb-school-list-header").length) {
                        	$(".gb-school-list-header").first().find(".gb-school-list-header-inner").focus().click();
                        } else {
                        	$(this).parent().next().find(".gb-school-list-header-inner").focus().click();
                        }
                    break;

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	if(e.shiftKey) {

                        } else {
                        	e.preventDefault();

                            $(".gb-school-list-section").eq($(this).index()).find(":focusable").eq(0).focus();
                        }
                    break;
                }
            });

            $(document).on("keydown", ".cs-mystart-dropdown.schools a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	if(e.shiftKey) {

                        } else {
                        	e.preventDefault();

                            var index = $(this).parents(".gb-school-list-section").find(":focusable").index(this)+1;
                        	var totalElements = $(this).parents(".gb-school-list-section").find(":focusable").length;

                        	// IS LAST ITEM
                        	if(index == totalElements) {
                            	$("#gb-schools-close-button").focus();
                            } else {
                            	$(this).parents(".gb-school-list-section").find(":focusable").eq(index).focus();
                            }
                        }
                    break;
                }            	
            });

            $(document).on("click keypress", "#gb-schools-close-button", function(e) { 
                if(_this.AllyClick(e)) {
                    $(".cs-mystart-dropdown.schools .cs-selector").attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");
                }
            });
        },

