                	index = index-1;
                }

                if(index <= 4) {
                	var iconNumber = channelGlobalIcons[index][0];
                	if(iconNumber == "0;1;2;3;4;5;6;7;8") {
                    	iconNumber = 8;
                    } else {
                    	iconNumber = parseInt(iconNumber, 10);
                    }

                    if(iconNumber > 0) {                    
                        $(".sw-channel-dropdown", this).creativeIcons({
                            "iconNum"       : iconNumber,
                            "defaultIconSrc": "",
                            "icons"         : channelGlobalIcons[index][1],
                            "siteID"        : "1",
                            "siteAlias"     : "loudoun",
                            "calendarLink"  : "/Page/2",
                            "contactEmail"  : "WebAdministrator@lcps.org",
                            "allLoaded"     : function() {
                                $(".cs-global-icons", this).prependTo($(this));
                            }
                        });

                        if($(".sw-channel-dropdown-inner li", this).length == 0) {
                        	$(".sw-channel-dropdown-inner", this).remove();
                    	} 

                    } else if($(".sw-channel-dropdown li", this).length == 0) {
                        $(this).addClass("no-dropdown");
                    }    
                } else if($(".sw-channel-dropdown li", this).length == 0) {
                	$(this).addClass("no-dropdown");
                } 
            });

            $(".sw-channel-item").on('mouseenter mouseleave', function (e) {
                if ($('.sw-channel-dropdown', this).length) {
                    var elm = $('.sw-channel-dropdown', this);
                    var off = elm.offset();
                    var l = off.left;
                    var w = elm.width();
                    var docH = $("header").height();
                    var docW = $("header").width();

                    var isEntirelyVisible = (l + w <= docW);

                    if (!isEntirelyVisible) {
                        $(this).addClass('edge');
                    } else {
                        $(this).removeClass('edge');
                    }
                }
            });
        },

        "ChannelBarActions": function() {
            // FOR SCOPE
            var template = this;

          	$(document).on("keydown", ".sw-channel-dropdown .cs-global-icon", function(e) {            
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();
                        var index = $(this).parents(".cs-global-icons").find(":focusable").index(this)-1;

                        // IS FIRST ITEM
                        if(index == -1) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).parents(".cs-global-icons").find(":focusable").attr("tabindex", "-1");
                            $(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parents(".cs-global-icons").find(":focusable").attr("tabindex", "-1");
                            $(this).parents(".cs-global-icons").find(":focusable").eq(index).attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        var index = $(this).parents(".cs-global-icons").find(":focusable").index(this)+1;
                        var totalElements = $(this).parents(".cs-global-icons").find(":focusable").length;

                        // IS LAST ITEM
                        if(index == totalElements) {
                            // FOCUS FIRST ITEM
                            $(this).parents(".cs-global-icons").find(":focusable").attr("tabindex", "-1");
                            $(this).parents(".sw-channel-dropdown").find(".sw-channel-dropdown-inner :focusable").eq(0).attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parents(".cs-global-icons").find(":focusable").attr("tabindex", "-1");
                            $(this).parents(".cs-global-icons").find(":focusable").eq(index).attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).parents(".cs-global-icons").find(":focusable").attr("tabindex", "-1");
                            $(this).parents(".sw-channel-item").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).parents(".cs-global-icons").find(":focusable").attr("tabindex", "-1");
                        $(this).parents(".cs-global-icons").find(":focusable:first-child").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).parents(".cs-global-icons").find(":focusable").attr("tabindex", "-1");
                        $(this).parents(".cs-global-icons").find(":focusable:last-child").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
     				case template.KeyCodes.esc:
                    	e.preventDefault();

                    	// CLOSE MENU
                    	$(this).parents(".cs-global-icons").find(":focusable").attr("tabindex", "-1");
                        $(this).parents(".sw-channel-item").find("> a").focus(); 
					break;
                }
            });        	
        },

