            function setCookie(name, value, days) {
                if (days) {
                    var date = new Date();
                    date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                    var expires = "; expires=" + date.toGMTString();
                } else var expires = "";
                document.cookie = name + "=" + value + expires + "; path=/";
            }

            function getCookie(name) {
                var nameEQ = name + "=";
                var ca = document.cookie.split(';');
                for (var i = 0; i < ca.length; i++) {
                    var c = ca[i];
                    while (c.charAt(0) == ' ') c = c.substring(1, c.length);
                    if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
                }
                return null;
            }

            var filter = getCookie('filterCookie');
            if (filter) {
                if (filter=="Search Entire District") { 
                    document.getElementById('cs-filter-district').click(); 
                } else if (filter=="Search Subsite Only") { 
                    document.getElementById('cs-filter-subsite').click();
                }
            }

            var district = document.getElementById('cs-filter-district');
            var subsite = document.getElementById('cs-filter-subsite');
            if (subsite.checked) {
                $.csSiteSpecificSearch({
                  "siteID": 1,
                  "districtSiteID": 1,
                  "filterDistrictResults": true
                });
            }

            $("#gb-search-form").submit(function(e){
                e.preventDefault();
                if($("#gb-search-input").val() != "I'm looking for..." && $("#gb-search-input").val() != "") {
                    if (district.checked) {
                        setCookie('filterCookie', district.value);
                    } else if (subsite.checked) {
                        setCookie('filterCookie', subsite.value);
                    }
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
            });

            $("#rs-menu-search-form").submit(function(e){
                e.preventDefault();
                if($("#rs-menu-search-input").val() != "Keyword Search" && $("#rs-menu-search-input").val() != "") {
                    if (district.checked) {
                        setCookie('filterCookie', district.value);
                    } else if (subsite.checked) {
                        setCookie('filterCookie', subsite.value);
                    }
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
            });

            $(document).on("keydown", "#gb-search-submit-btn", function(e) {
                if(e.shiftKey && e.keyCode == _this.KeyCodes.tab) {

                } else if(e.keyCode == _this.KeyCodes.tab) {
                	e.preventDefault();
                    $(".gb-search-btn").focus();
                }
            });

			$("#gb-search-input").focus(function() {
				if($(this).val() == "I'm looking for...") {
					$(this).val("");
				}
			}).blur(function() {
				if($(this).val() == "") {
					$(this).val("I'm looking for...");
				}
			});
        },

        /* General Methods */
        "AllyClick": function(event) {
            if(event.type == "click") {
            	event.preventDefault();
                return true;
            } else if(event.type == "keypress" || event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
            	event.preventDefault();
                return true;
            } else {
                return false;
            }
        },

        "CutHex": function(hex){
        	return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },

        "LoadImage": function(element, image, defaultImage, toggle) {
        	//Variables
            image = $.trim(image);

            if(toggle) {
            	var srcCheck = image.indexOf("Faces/default-man.jpg") > -1;

                if((image != "") && (!srcCheck)) {
                    $(element).append('');
                } else {
                    $(element).append('');
                }
            }

        },

        "GatherRSMenuLinks": function(element) {
        	var links = [];

        	$(element).each(function() {
            	links.push({ "text": $(this).text(), "url": $(this).attr("href") });
            });

            return links;
        }
    };

