    function csInit298921() {
    	switch(csAppSettings298921.csInitViewOptions) {
        	case "1":
            	//nothing to be done here
            break;
            case "2":
            	$("#pmi-291027 .ui-widget.app.content-accordion .ui-article").addClass("active").attr("aria-expanded", "true");
                $("#pmi-291027 .ui-widget.app.content-accordion .ui-article .ui-article-detail").css("display","block");
            break;
            case "3":
            	$("#pmi-291027 .ui-widget.app.content-accordion li:eq(0) .ui-article").addClass("active").attr("aria-expanded", "true");
            	$("#pmi-291027 .ui-widget.app.content-accordion li:eq(0) .ui-article .ui-article-detail").css("display","block");
            break;
        }

        switch(csAppSettings298921.csViewOptions) {
        	case "1":
                $("#pmi-291027").on("click keydown", ".ui-article-title", function(e) {
                    if(allyClick(e)) {
                        e.preventDefault();
                        if($(this).parent().hasClass("active")){
                            $(this).parent().find(".ui-article-detail").slideUp();
                            $(this).parent().removeClass("active");
                            $(this).attr("aria-expanded", "false");
                        } else {
                            $(this).parent().find(".ui-article-detail").slideDown();
                            $(this).parent().addClass("active");
                            $(this).attr("aria-expanded", "true");
                        }
                    }
                });
            break;
            case "2":
				$("#pmi-291027").on("click keydown", ".ui-article-title", function(e) {
                    if(allyClick(e)) {
                        e.preventDefault();

                        if($(this).parent().hasClass("active")){
                            $(this).parent().find(".ui-article-detail").slideUp();
                            $(this).parent().removeClass("active");
                            $(this).attr("aria-expanded", "false");
                        } else {
                            $("#pmi-291027 .ui-widget.app.content-accordion .active .ui-article-detail").slideUp();
                            $("#pmi-291027 .ui-widget.app.content-accordion .active").removeClass("active");
                            $(this).parent().find(".ui-article-detail").slideDown();
                            $(this).parent().addClass("active")
                            $(this).attr("aria-expanded", "true");
                        }
                    }
                });
            break;
        }

        $("#pmi-291027").on("click keydown", ".cs-accordion-expand-toggle", function(e) {
        	if(allyClick(e)) {
            	e.preventDefault();

                if($(this).hasClass("expand")) {
                	$(this).removeClass("expand").addClass("collapse").text("Collapse All").attr("aria-label", "Collapse All Accordion Items");
                	$("#pmi-291027 .ui-article-detail").slideDown();
