link.script:
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'UA-9241562-9');

    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "HeaderIsSticky": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.Header();
            this.StickyHeader();
            this.MyStart();
            this.SchoolList();           
            this.Body();
            this.UpcomingEvents();
            this.Footer();
            this.Slideshow();
            this.StreamingVideo();

            this.Search();
            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
 			$("#cs-contrast-bar-left-inner").html($("div#cs-contrast-bar-outer #cs-contrast-bar").html());
            $("div#cs-contrast-bar-outer").remove();

            if($(".hp").length) {
            	this.FixedElements();
            }

            this.ChannelBar();
            this.ChannelBarActions();

            $("div.ui-widget.app.navigation li").hover(function() {
            	$(this).addClass("hover");
            }, function() {
            	$(this).removeClass("hover");
            });

            $("div.ui-widget.app.navigation a").on("focus", function() {
            	$(this).parent().addClass("hover");
            }).on("blur", function() {
             	$(this).parent().removeClass("hover");           
            });

            $('#gb-sitename').css('padding-left', $('#gb-logo').width() + 18);

            if($(window).width() < 1024) {
            	if($(".cs-mystart-dropdown.search #cs-search-filter").length) {
            		$(".cs-mystart-dropdown.search #cs-search-filter").appendTo("#rs-menu-search");
                }
                if($(".cs-mystart-dropdown.search #gb-search-form").length) {
            		$(".cs-mystart-dropdown.search #gb-search-form").prependTo("#rs-menu-search");
                }
            } else {
            	if($("#rs-menu-search #cs-search-filter").length) {
            		$("#rs-menu-search #cs-search-filter").appendTo(".cs-mystart-dropdown.search .cs-dropdown-list");
                }
                if($("#rs-menu-search #gb-search-form").length) {
            		$("#rs-menu-search #gb-search-form").appendTo(".cs-mystart-dropdown.search .cs-dropdown-list");
                }
            }

            $(window).resize(function() {
            	if($(window).width() < 1024) {
                    if($(".cs-mystart-dropdown.search #cs-search-filter").length) {
                        $(".cs-mystart-dropdown.search #cs-search-filter").appendTo("#rs-menu-search");
                    }
                    if($(".cs-mystart-dropdown.search #gb-search-form").length) {
                        $(".cs-mystart-dropdown.search #gb-search-form").prependTo("#rs-menu-search");
                    }
                } else {
                    if($("#rs-menu-search #cs-search-filter").length) {
                        $("#rs-menu-search #cs-search-filter").appendTo(".cs-mystart-dropdown.search .cs-dropdown-list");
                    }
                    if($("#rs-menu-search #gb-search-form").length) {
                        $("#rs-menu-search #gb-search-form").appendTo(".cs-mystart-dropdown.search .cs-dropdown-list");
                    }
                }
            });

        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyHeader();

            $('#gb-sitename').css('padding-left', $('#gb-logo').width() + 18);

        },

        "WindowScroll": function() {
        	var _this = this;

            this.StickyHeader();

            if($(".hp").length) {
            	this.FixedElements();
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":
                case "640":
                case "480":
                case "320":

                break;
            }
        },

       "FixedElements": function() {
       		//Variables
            var slideshowTop = $("#hp-slideshow-outer").offset().top;
            var slideshowBottom = slideshowTop + $("#hp-slideshow-outer").outerHeight(true);
            var scrollTop = $(window).scrollTop();
            var scrollBottom = scrollTop + $(window).height();

            if(scrollBottom > slideshowBottom) {
            	$("#hp-slideshow .mmg-description-outer, #hp-slideshow .mmg-controls, #gb-global-icons-outer, #gb-scroll-content-button, .cs-fullscreen-video-buttons").removeClass("gb-fixed");
            } else {
                $("#hp-slideshow .mmg-description-outer, #hp-slideshow .mmg-controls, #gb-global-icons-outer, #gb-scroll-content-button, .cs-fullscreen-video-buttons").addClass("gb-fixed");
            }

            if($(window).scrollTop() < $("#hp-slideshow-outer").outerHeight(true)+ $("#gb-global-icons-outer").outerHeight(true)) {
                $("#gb-global-icons-outer").removeClass("gb-sticky");
            } else {
                $("#gb-global-icons-outer").addClass("gb-sticky");
            }
        },

        "StickyHeader": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("header").outerHeight();
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.HeaderIsSticky) {
                                $("header").removeClass("fixed-element").removeAttr("style");
                                $("#gb-page").css("padding-top", 0);

                                this.HeaderIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("header").css("height", $("#gb-header-outer").outerHeight(true));
                            $("#gb-page").css("padding-top", $("header").outerHeight(true));

                            if(!this.HeaderIsSticky) {
                                $("header").addClass("fixed-element");

                                this.HeaderIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("header").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("nav").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.HeaderIsSticky) {
                            $("header").removeClass("fixed-element").removeAttr("style");
                            $("#gb-page").css("padding-top", 0);

                            this.HeaderIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }

                $('#gb-sitename').css('padding-left', $('#gb-logo').width() + 18);
            }
        },

