        /* General Methods */
        "AllyClick": function(event) {
            if(event.type == "click") {
            	event.preventDefault();
                return true;
            } else if(event.type == "keypress" || event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
            	event.preventDefault();
                return true;
            } else {
                return false;
            }
        },

        "CutHex": function(hex){
        	return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },

        "LoadImage": function(element, image, defaultImage, toggle) {
        	//Variables
            image = $.trim(image);

            if(toggle) {
            	var srcCheck = image.indexOf("Faces/default-man.jpg") > -1;

                if((image != "") && (!srcCheck)) {
                    $(element).append('');
                } else {
                    $(element).append('');
                }
            }

        },

        "GatherRSMenuLinks": function(element) {
        	var links = [];

        	$(element).each(function() {
            	links.push({ "text": $(this).text(), "url": $(this).attr("href") });
            });

            return links;
        }
    };

