                 case 'jul':
                    myMonth = 6;
                    break;
                 case 'aug':
                    myMonth = 7;
                    break;
                 case 'sep':
                    myMonth = 8;
                    break;
                 case 'oct':
                    myMonth = 9;
                    break;
                 case 'nov':
                    myMonth = 10;
                    break;
                 case 'dec':
                    myMonth = 11;
                    break;
