        	this.JsMediaQueries();
        	csGlobalJs.OpenInNewWindowWarning();
            // REMOVE CLEAR DIV IN APP FOOTER
            $(".ui-widget-footer").each(function() {
            	var thisFooter = this;
                $(".clear",thisFooter).remove();
