$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=wAyHKxH2DuHlfdtEPsSpeA%3d%3d&SessionID=5e1dff3c-ae7c-422e-b0ca-99488c64e547&SiteID=4&ChannelID=807&SectionID=1983&PageID=7342&HitDate=4%2f22%2f2020+1%3a07%3a44+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.70.3';
