//Convert numeric month to alpha months
  $(document).ready(function () {
      var d = new Date();
      var months = ["JAN", "FEB", "MAR", "APR", "MAY", "JUN", "JUL", "AUG", "SEP", "OCT", "NOV", "DEC"];
      document.getElementById('TodaysDate').innerHTML = months[d.getMonth()] + " " + d.getDate() + ", " + d.getFullYear();
      document.getElementById('TodaysDate2').innerHTML = months[d.getMonth()] + " " + d.getDate() + ", " + d.getFullYear();
  });
//Convert to Black and white for low contrast
  function contrast() {
      var loc = window.location.href;
      var txt = "contrast";
      if(loc.indexOf(txt)> -1)
      {
          var res = loc.split("?");
          $(location).attr('href', res[0]);
      }
      else
          { $(location).attr('href', window.location.href + "?#contrast");}
  }
  $(document).ready(function () {
      var loc = window.location.href;
      var txt = "contrast";
      if(loc.indexOf(txt)> -1)
      {
          $('head').append('');
          $('#contrastCB').prop('checked', true);
          $('a').each(function () {
              var href = $(this).attr('href');
              var idLink = $(this).attr('id');
              if (href && idLink != "skipLink") {
                  href += (href.match(/\?/) ? '&' : '?') + "#contrast";
                  $(this).attr('href', href);
              }
          });
      }
  });
  $(document).ready(function () {
      $('.gsc-branding-img').attr('alt', 'Google Branding Image');
  })

