        "Search": function() {
        	var defSearchText = "search...";
            var searchBox = $('#swsitesearch-txt-searchstring');
            searchBox
                .attr('tabindex', -1)
                .attr("aria-label", "Search input")
                .attr("placeholder", defSearchText)
                .val(defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }

                    searchBox.off('keydown keyup keypress');
                });
        	$('#header-search-trigger').off('click').click(function(e){
            	if( searchBox.hasClass('showing') ){
                	searchBox
                    	.attr('tabindex', -1)
                        .animate({
                            'width': 0,
                            'border-color': 'transparent'
                        }, 300);

                    $(this)
                        .attr('aria-label', 'Open search container')
                        .attr('aria-expanded', 'false');

                    searchBox.removeClass('showing');
                }else{
                    searchBox
                    	.attr('tabindex', 0)
                        .animate({
                            'width': 100,
                            'border-color': '#823f98'
                        }, 300, function(){
                            searchBox.focus();
                        });

                    $(this)
                    	.attr('aria-label', 'Close search container')
                        .attr('aria-expanded', 'true');

                    searchBox.addClass('showing');
				}
            });
        },
        "StickyHeader": function() {
        	var getWidth = $(window).width();
            if (getWidth >= 1024) {
                // CHANNEL BAR
                var gbNavOffSet = $(".sticky-container").offset().top;

                if ($(window).scrollTop() <= gbNavOffSet) {
                    if(csNavIsSticky) {
                        $(".sticky-container, #gb-page").removeClass("sticky-on");
                        $(".sticky-container").css("height", "");
                        csNavIsSticky = 0;
                    }
                } else {
                    if(!csNavIsSticky) {
                        $(".sticky-container").css("height", $(".sticky-item").outerHeight());

                        setTimeout(function(){
                            $(".sticky-container").css("height", $(".sticky-item").outerHeight());
                        }, 100);

                        $(".sticky-container, #gb-page").addClass("sticky-on");
                        csNavIsSticky = 1;
                    }
                }

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $(".sticky-item").outerHeight(true));
                    }
                }
            } else {
                if(csNavIsSticky == 1) {
                    $(".sticky-container, #gb-page").removeClass("sticky-on");
                    $(".sticky-container").css("height", "");

                    csNavIsSticky = 0;
                }
            }
        },


