        $(".hp.column.one .headlines").each(function() {
        	$(".ui-article.cs-has-thumb",this).each(function() {
            	var titleHeight = $(".ui-article-title",this).height();
                var descHeight = $(".ui-article-description",this).height();
                var thumbHeight = $(".ui-article-thumb",this).height();
                var getThis = this;

                if((titleHeight + descHeight) > thumbHeight) {
                	$(".custom-teaser-read-more",getThis).css({
                    	"display": "block"
                    });
                }
            });
        });

        $(".sw-mystart-dropdown.translate.branded").unbind("hover");
        $(".sw-mystart-dropdown.translate.branded").mouseleave(function(e){
            if(e.target.nodeName.toLowerCase() !== 'select') {
                $(".sw-dropdown-arrow").removeClass("rotate-arrow");
                $(".sw-dropdown",this).slideUp("slow", "swing");
                $(this).removeClass("open");
            }
        });

        csGlobalJs.OpenInNewWindowWarning();
	});

    $(window).scroll(function() {
        var scrolled = $(window).scrollTop();
        var channelBarHeight = $("#gb-channel-list-outer").height();
        var gbHeaderHeight = $("#gb-header-outer").height();

        if($(".hp").length) {
          var slideshowHeight = $("#hp-slideshow").height();

          if($(window).width() >= 1024) {
          	if(scrolled >= 100) {
              scrolled = scrolled - 100;
              $("#hp-slideshow-outer").addClass("fixed2");
              //$("#gb-header-outer").addClass("fixed");
              $("#hp-content-outer").css("margin-top",slideshowHeight);
              $('#hp-slideshow-outer').css({
                  'top': -(scrolled*0.5) + 'px'
              });
              /*$('#gb-header-outer').css({
                  'top': +(scrolled*1) +'px'
              });*/
              $("#gb-global-icons-outer").addClass("fixed-icons").css({
              	  'position':'fixed',
                  'bottom': +(scrolled*1) + 122 + 'px'
              });

              /*if(scrolled > 153) {
              	$('#gb-logo').fadeOut();
                $('#sw-mystart-outer').css({
                  'z-index': 0
              	});
              } else {
              	$('#gb-logo').fadeIn();
              }*/
            } else {
              $("#hp-slideshow-outer").removeClass("fixed2");
              $("#gb-header-outer").removeClass("fixed");
              $("#hp-content-outer").css("margin-top","0");
              $("#hp-slideshow-outer").css({
              	"top": "0"
              });
              /*$("#gb-header-outer").css({
              	"top": "0"
              });
              $('#gb-logo').fadeIn();
              $('#sw-mystart-outer').css({
                'z-index': 8000
              });*/
              $("#gb-global-icons-outer").removeClass("fixed-icons").css({
              	  'position':'absolute',
                  'bottom': '22px'
              });
              var setSlideshowHeight = $(window).height() - 100;
              $("#hp-slideshow .rs-photo-gallery-container, #hp-slideshow .rs-photo-gallery-images, #hp-slideshow .rs-photo-gallery-images ul, #hp-slideshow .rs-photo-gallery-images ul li").css({
              	"height": setSlideshowHeight
              });
          	}
          }

          /*if($(window).width() >= 1024 && scrolled >= 100) {
              scroller = $(window).scrollTop() - 100;
              $('#hp-slideshow-outer').css({
                  'top': -(scroller*0.7)+'px'
              });
          } else {
          	$("#hp-slideshow-outer").css("top","0");
          }*/
        }

        if($(window).scrollTop() > 0) {
                $("#hp-slideshow-outer .scroll-more-slideshow").fadeOut();
            } else {
                $("#hp-slideshow-outer .scroll-more-slideshow").fadeIn();
            }
    });

    function headlinesAppScroller() {
    	if(csWindowWidth >= 1280) {
          $("#sw-content-container3.region.ui-hp div.ui-widget.app.headlines").appScroller({
              "appParent"             : "#hp-content-bottom",
              "targetApp"             : "headlines", //upcomingevents or headlines
              "imageNum960"           : 4, //NUMBER OF IMAGES TO DISPLAY AT 960
              "dynamicWidth"          : "no",
              "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
              "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
              "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
              "extraBackIconClass"    : "insight-icon-font border-box",
              "extraNextIconClass"    : "insight-icon-font border-box",
              "controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
          });
          if($("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").length <=  4) {
              $("#hp-content-bottom .headlines-scroll-ctrl").hide();
          }
      	} else {
        	$("#sw-content-container3.region.ui-hp div.ui-widget.app.headlines").appScroller({
              "appParent"             : "#hp-content-bottom",
              "targetApp"             : "headlines", //upcomingevents or headlines
              "dynamicWidth"          : "yes",
              "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
              "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
              "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
              "extraBackIconClass"    : "insight-icon-font border-box",
              "extraNextIconClass"    : "insight-icon-font border-box",
              "controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
        });
      }

      $("#hp-content-bottom .ui-widget.app.headlines").each(function() {
          $(".ui-article", this).each(function() {
              if(!$(".cs-article-content-cont",this).length) {
                  $("
").prependTo(this);
              }

              var headlineURL = $("h1 a", this).attr("href");
              if(!$(".custom-teaser-read-more",this).length) {
              	$(".cs-article-content-cont",this).append('î¤‡');
              }

              var headlineDesc = $("p",this).text();
              if(!$(".cs-headline-desc",this).length) {
              	$(".cs-article-content-cont",this).prepend("
" + headlineDesc + "");
              }

              var headlineTitle = $("h1 a span",this).text();
              if(!$(".cs-headline-title",this).length) {
              	$(".cs-article-content-cont",this).prepend("
" + headlineTitle + "");
              }

              var contHeight = $(".cs-article-content-cont",this).height();
              var titleHeight = $(".cs-headline-title",this).height();
              $(".cs-article-content-cont",this).css({
                  "bottom": "-" + contHeight + "px"
              });

              var newBottom = (contHeight - titleHeight) - 50;
              $(this).hover(function() {
                  var $this = this;
                  $(".cs-article-content-cont",this).css({
                      "bottom": "-" + newBottom + "px"
                  });
              }, function() {
                  $(".cs-article-content-cont",this).css({
                      "bottom": "-" + contHeight + "px"
                  });
              });

              $(".cs-article-content-cont",this).hover(function() {
                  $(this).css({
                      "bottom": "0px"
                  });
                  $("#hp-content-bottom .custom-teaser-read-more span").css({
                      "top": "50%",
                      "margin-top": "-17px"
                  });
              }, function() {
                  $(this).css({
                      "bottom": "-" + newBottom + "px"
                  });
                  $("#hp-content-bottom .custom-teaser-read-more span").css({
                      "top": "10px",
                      "margin-top": "0"
                  });
              });
          });
      });
    }

	$(function(){
