	function logoWidth() {
		if($("#gb-logo img").length) {
			var imgWidth = $("#gb-logo img").width();
			$("#gb-logo").width(imgWidth);
		}
	}

	function logoWidth480() {
    	var getLogoWidth = $("#gb-logo img").width();
		if($("#gb-logo img").length) {
			var imgWidth = getLogoWidth*.75;
			$("#gb-logo, #gb-logo img").width(imgWidth);
		}
	}

	function appView(){
    	if(!$("#hp-content-top div.ui-widget.app.active").length) {
        	$("#hp-content-top div.ui-widget.app").first().addClass("active");
        }

		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-top div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
        $('#hp-content-top .ui-widget-header')
        	.off('click') //these needs to be here to remove click events when mobile devices scroll (which causes a screen resize event)
            .click(function(){
              if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
                  $(this).parent().removeClass("active");
              }else{
                  var clickedApp = $(this).parent().parent().parent().attr("id");
                  $(this).parent().addClass("active");
                  smoothScroll("#" + clickedApp, 3);
              }
       		});
	}	

    function smoothScroll( to, offset ){
        if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
        var target = $(to);

        if (target.length) {
            $('html,body').animate({
                scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
            }, 800, function(){
                $("html, body").stop(true, false);
            });
            return false;
        }
    }

