$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=I79EYKeJT0utyrJMPDL27w%3d%3d&SessionID=a3aaeba0-f6c3-4a52-ba13-85501b6f0be0&SiteID=159&ChannelID=0&SectionID=159&PageID=1232&HitDate=4%2f22%2f2020+12%3a23%3a24+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.87.69';
