$(function() {
	$.schoolListMinibase({
		'miniBaseDomainID': 4,
		'miniBaseModuleInstanceID': 2726,
		'cache': true,
		'schoolListReady': function(schoolList) {
			/*
			The returned value is an array of objects and each object is a school record.
			The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
			*/

			var schools = '';

			for(var i in schoolList) {
				if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
					schools += '
' + schoolList[i].schoolName + '';
				}
			}

			$('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
		}
	});
});
$(window).load(function(){
 $('#hp-content-top, #hp-content-bottom, footer').show();
});

    var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
    var mediaType = 'MMR';

    window.customHeadlines = function($elem) {
        var getLeft = parseInt($elem.css("left"));
        var temp = -1 * $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").width();

        if(getLeft < temp) {
            getLeft = $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles").width();
            $elem.css("left", getLeft);
        }
    }

    $(window).load(function() {
        searchText();
        //ACCESSIBLE NAV APP
        $(".ui-widget.app.navigation").csAccessibleNavigationApp();

        //HIDE EMPTY DROPDOWN THAT GETS PLACED BY THE SYSTEM IF CHANNEL HAS NO SECTIONS BUT CHANNEL HOMEPAGE IS TURNED ON
        $("ul.sw-channel-dropdown").each(function() {
            var systemDropdown = $(this);
            if(!$("li",systemDropdown).length) {
                $(systemDropdown).remove();
            }
        });

        //DOC ADD HAS-DROPDOWN CLASS TO CHANNELS THAT HAVE A SECTION DROPDOWN
        $("li.sw-channel-item").each(function() {
            if($(".sw-channel-dropdown", this).length) {
                $(this).addClass("has-dropdown");
            }
        });

        customMegaMenu();


        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
        $(".sw-channel-item").unbind('hover');
        $(".sw-channel-item").hover(function(){
            var subList = $(this).children('ul');
            if ($.trim(subList.html()) !== "") {
                subList.stop(true,true).slideDown(300, "swing");
            }
            $(this).addClass("hover");
        }, function(){
            $(".sw-channel-dropdown").slideUp(300, "swing");
            $(this).removeClass("hover");
        });

        $(".sw-channel-dropdown .sections li a").keydown(function(e) {
            var thisParent = $(this).parent().parent().parent().parent();
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        //$(".sw-mystart-button.user-options").focus();
                        //$(this).parent().parent().parent().slideDown();
                        $(".additional-links li:last-child a",thisParent).focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    $(".sw-channel-dropdown .sections li:first-child > a").focus();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(".mega-channel-btn",thisParent).focus();
                    } else {
                        // FOCUS NEXT ITEM

                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(".sw-channel-dropdown .sections li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(".sw-channel-dropdown .sections li:last-child > a").focus();
                break;

                // CONSUME TAB KEY
                case csKeyCodes.tab:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(this).parent().parent().parent().parent().parent().parent().next().find("> a").focus();
                    //$("li.sw-channel-item.first").focus();

                break;

            }
        });

        $(".sw-channel-dropdown .additional-links li a").keydown(function(e) {
            var thisParent = $(this).parent().parent().parent().parent();
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        //$(".sw-mystart-button.user-options").focus();
                        //$(this).parent().parent().parent().slideDown();
                        $(".mega-channel-btn",thisParent).focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    $(".sw-channel-dropdown .additional-links li:first-child > a").focus();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(".sw-channel-dropdown .sections li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM

                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(".sw-channel-dropdown .additional-links li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(".sw-channel-dropdown .additional-links li:last-child > a").focus();
                break;

                // CONSUME TAB KEY
                case csKeyCodes.tab:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(this).parent().parent().parent().parent().parent().parent().next().find("> a").focus();

                break;

            }
        });

        $(".sw-channel-dropdown .mega-column.one a").keydown(function(e) {
            var thisParent = $(this).parent().parent();
            switch(e.keyCode) {
              case csKeyCodes.down:
                    e.preventDefault();
                    $(".sw-channel-dropdown .additional-links li:first-child > a").focus();
                break;
              case csKeyCodes.up:
                    e.preventDefault();
                    $(".sw-channel-dropdown .sections li:last-child > a").focus();
                break;

            }
        });

        MyStartDropdownActions({
            "dropdownParent": ".sw-mystart-dropdown",
            "dropdownSelector": ".selector",
            "dropdown": ".sw-dropdown",
            "dropdownList": ".sw-dropdown-list"
        });

        $("#hp-content-bottom .more-link-under").remove();
        if($(window).width() > 1023) {
            $("#hp-content-bottom .ui-widget.app.headlines").each(function() {
                var scrollerHeadlines = this;
                if(!$(scrollerHeadlines).find(".ui-articles li.last-article").length) {
                	$(scrollerHeadlines).find(".ui-articles li").last().addClass("last-article");
                }
                $(scrollerHeadlines).find(".ui-articles li.last-article").prependTo($(scrollerHeadlines).find(".ui-articles"));
            });

            ArticleSlider();
        } else {
        	$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active-article").removeClass("active-article");

        	ArticleSliderMobile();
        }




        jsMediaQueries();

        $("#sw-mystart-outer").remove();

        csGlobalJs.OpenInNewWindowWarning();
    });

    $(function(mediaType){
        $(window).bind("resize", function(){ 
            jsMediaQueries(); 
        });

        jsMediaQueries();
        useChannelForSection();
        dynamicContent();
        globalIcons();
        if(mediaType = "MMR") {
            mediaRotator();	
        }
        appAccordion();
        csRsMenu();
        $(".sw-mystart-dropdown").unbind().removeAttr("tabindex aria-expanded aria-label role");
        $(".sw-mystart-dropdown a").unbind();
        $(".sw-mystart-dropdown .sw-dropdown").attr("aria-hidden", "true");
        $(".sw-mystart-dropdown .selector").removeAttr("aria-hidden").attr({
            "role": "button",
            "aria-expanded": "false",
            "aria-haspopup": "true",
            "tabindex": "0"
        });

		csGlobalJs.OpenInNewWindowWarning();
    });

    function ArticleSlider() {
        $("#hp-content-bottom .ui-widget.app.headlines").csArticleSlider({
            "viewerNum": [3, 1, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
            "transitionSpeed": .5 // SECONDS
        });	

        $(".cs-article-slider-control.back, .cs-article-slider-control.next").addClass("littleRock-icon-font").html("î¤");

        $("#hp-content-bottom .ui-widget.app.headlines").each(function() {
            var thisHeadlines = this;
            if(!$(".ui-articles li.active-article", thisHeadlines).length) {
                $(".ui-articles li:eq(1)", thisHeadlines).addClass("active-article");
            }
            $(".cs-article-slider-control.next", thisHeadlines).on("click keydown", function() {
                $("li.active-article",thisHeadlines).removeClass("active-article").next().addClass("active-article");

                $(".active-article-info").empty();
                $(".active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
                $(".active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");

            });
            $(".cs-article-slider-control.back", thisHeadlines).on("click keydown", function() {
                $("li.active-article",thisHeadlines).removeClass("active-article").prev().addClass("active-article");

                $(".active-article-info").empty();
                $(".active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
                $(".active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");

            });

            if(!$(".active-article-info", thisHeadlines).length) {
              $("
").appendTo("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail");
              $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
              $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");
            }
        });
    }

    function ArticleSliderMobile() {
        $("#hp-content-bottom .ui-widget.app.headlines").csArticleSlider({
            "viewerNum": [3, 1, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
            "transitionSpeed": .5 // SECONDS
        });	

        $(".cs-article-slider-control.back, .cs-article-slider-control.next").addClass("littleRock-icon-font").html("î¤");

        $("#hp-content-bottom .ui-widget.app.headlines").each(function() {
            var thisHeadlines = this;

            if(!$(".ui-articles li.active-article", thisHeadlines).length) {
                $(".ui-articles li:eq(0)", thisHeadlines).addClass("active-article");
            }

            $(".cs-article-slider-control.next", thisHeadlines).on("click keydown", function() {
                $("li.active-article",thisHeadlines).removeClass("active-article").next().addClass("active-article");

                $(".active-article-info").empty();
                $(".active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
                $(".active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");

            });
            $(".cs-article-slider-control.back", thisHeadlines).on("click keydown", function() {
                $("li.active-article",thisHeadlines).removeClass("active-article");
                $("li", thisHeadlines).last().addClass("active-article");

                $(".active-article-info").empty();
                $(".active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
                $(".active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");

            });

            if(!$(".active-article-info", thisHeadlines).length) {
              $("
").appendTo("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail");
              $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
              $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");
            }
        });
    }

    function jsMediaQueries() {
        switch(csGetBreakPoint()) {
            case "desktop": 
                //headlinesScroller();
            break;
            case "768":
                //headlinesScrollerMobile();
            break;
            case "640":
                //headlinesScrollerMobile();
            break;
            case "480":
                //headlinesScrollerMobile();
            break;
            case "320":
               //headlinesScrollerMobile();
            break;
        }
    }

    function headlinesScroller() {
        if($("#hp-content-bottom").length) {
          $("#sw-content-container3.region.ui-hp div.ui-widget.app").appScroller({
                  "appParent"             : "#hp-content-bottom",
                  "targetApp"             : "headlines", //upcomingevents or headlines
                  "imageNum960"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 960
                  "imageNum768"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 768
                  "imageNum640"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 640
                  "imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
                  "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
                  "backIconFontLetter"    : "î¤", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                  "nextIconFontLetter"    : "î¤", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                  "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                  "extraBackIconClass"    : "littleRock-icon-font",
                  "extraNextIconClass"    : "littleRock-icon-font",
                  "controlGutter"         : "50" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
          });
        }

          $("#hp-content-bottom .headlines .ui-widget-header").after($("#hp-content-bottom .headlines-scroll-ctrl.back"));	
          $("#hp-content-bottom .ui-article-title").attr("aria-hidden","true");

          if($("#hp-content-bottom .more-link").length) {
              $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").each(function() {
                  var thisLI = this;
                  if(!$(".ui-article",thisLI).length) {
                      $(thisLI).remove();
                  }
              });
              $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").last().remove();
              $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").last().addClass("last-slide");
          }
          $("#hp-content-bottom .more-link-under").remove();
          if(!$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").last().hasClass("first-slide")) {
            $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.first-slide").clone().appendTo("#hp-content-bottom div.ui-widget.app.headlines .ui-articles");
          }
          if(!$("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").first().hasClass("last-slide")) {
            $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.last-slide").clone().prependTo("#hp-content-bottom div.ui-widget.app.headlines .ui-articles");
          }
          $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li:nth-child(2)").addClass("active-article");
          if(!$(".active-article-info").length) {
            $("
").appendTo("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail");
            $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
            $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");
          }


          var j = 0;
          $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").each(function () {
            var thisWidth = $(this).width();
                $(this).css("left", j);
                j += thisWidth;
                window.customHeadlines($(this));
          });

          $(".headlines-scroll-ctrl.next").click(function() {
              if($(".last-slide.active-article").length) {
                  $(".active-article").removeClass("active-article");
                  $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li:eq(1)").addClass("active-article");
              } else {
                  $(".active-article").delay(500).next().addClass("active-article");
                  $(".active-article:first-child").delay(500).removeClass("active-article");
                  $(".active-article:last-child").delay(500).removeClass("active-article");
                  //$(".active-article").removeClass("active-article").next().addClass("active-article");
              }

              $(".active-article-info").empty();
              $(".active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
              $(".active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");

          });
          $(".headlines-scroll-ctrl.back").click(function() {
              var numArticles = $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").length;
              var newnumArticles = numArticles - 2;
              if($(".first-slide.active-article").length) {
                  $(".active-article").removeClass("active-article");
                  $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li:eq("+newnumArticles+")").addClass("active-article");
              } else {
                  $(".active-article").removeClass("active-article").prev().addClass("active-article");
              }

              $(".active-article-info").empty();
              $(".active-article .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");
              $(".active-article .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");


          });
    }

    function headlinesScrollerMobile() {
        if($("#hp-content-bottom").length) {
          $("#sw-content-container3.region.ui-hp div.ui-widget.app").appScroller({
                  "appParent"             : "#hp-content-bottom",
                  "targetApp"             : "headlines", //upcomingevents or headlines
                  "imageNum960"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 960
                  "imageNum768"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 768
                  "imageNum640"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 640
                  "imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
                  "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
                  "backIconFontLetter"    : "î¤", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                  "nextIconFontLetter"    : "î¤", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                  "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                  "extraBackIconClass"    : "littleRock-icon-font",
                  "extraNextIconClass"    : "littleRock-icon-font",
                  "controlGutter"         : "50" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
          });
        }

        $("#hp-content-bottom .headlines .ui-widget-header").after($("#hp-content-bottom .headlines-scroll-ctrl.back"));	
        $("#hp-content-bottom .ui-article-title").attr("aria-hidden","true");

        if($("#hp-content-bottom .more-link").length) {
            $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").each(function() {
                var thisLI = this;
                if(!$(".ui-article",thisLI).length) {
                    $(thisLI).remove();
                }
            });
            $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").last().remove();
            $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").last().addClass("last-slide");
        }
        if(!$(".active-article-info").length) {
          $("
").appendTo("#hp-content-bottom div.ui-widget.app.headlines .ui-widget-detail");
          $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");;
          $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li.active .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");;
        }

        var j = 0;
        $("#hp-content-bottom div.ui-widget.app.headlines .ui-articles li").each(function () {
              $(this).css("left", j);
              j += 350;
              window.customHeadlines($(this));
        });

        $(".headlines-scroll-ctrl.next").click(function() {
              $(".active-article-info").empty();
              $(".active .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");;
              $(".active .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");;

          });
          $(".headlines-scroll-ctrl.back").click(function() {
              $(".active-article-info").empty();
              $(".active .ui-article-title").clone().prependTo(".active-article-info").attr("aria-hidden","false");;
              $(".active .ui-article-description").clone().appendTo(".active-article-info").attr("aria-hidden","false");;


          });  
    }

    function customMegaMenu() {

    	$("ul.sw-channel-list li.hidden-channel").remove(); /* added by Matt B - Case#3991496 - 10.10.18 */

        var megaMenuData = [
            [
                ["/cms/lib/AR02203631/Centricity/Template/GlobalAssets/images///LittleRock/channel-image.png", "", "", ""],
                ["", ""],
                ["", ""],
                ["", ""],
