                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""]
            ],
            [
                ["/cms/lib/AR02203631/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", "", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""]
            ],
            [
                ["/cms/lib/AR02203631/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", "", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""]
            ],
            [
                ["/cms/lib/AR02203631/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", "", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""]
            ],
            [
                ["/cms/lib/AR02203631/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", "", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""],
                ["", ""]
            ]
        ];

        //var getChannelHeader = $("li.sw-channel-item.hover > a span").text();

        // BUILD THE MEGA MENUS
        $("#sw-channel-list-container li.sw-channel-item").each(function(){
            // LOWERCASE THE CHANNEL NAME
            var channelText = $("> a span", this).text().toLowerCase();

            // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
            if(channelText == "home") {
                $(this).addClass("home");
            } else if(channelText == "calendar") {
                $(this).addClass("calendar");
            }

            //IGNORE HOME AND CALENDAR CHANNELS
            if(channelText != "home" && channelText != "calendar" && $(this).hasClass("has-dropdown")) {
                // SET THE CHANNELS INDEX
                var i = ($("#sw-channel-list-container li.sw-channel-item.home").length) ? $(this).index() - 1 : $(this).index();

                // ADD MEGA CHANNEL CLASS
                $(this).addClass("mega-channel");

                // ADD STRUCTURE TO THE DOM
                $(".sw-channel-dropdown", this).removeAttr("class").wrap('

');


                // ADD ADDITIONAL LINKS
                var linkColumn = '';
                linkColumn = '

Important Links';
                for(var n = 1; n <= 5; n++) {
                    if(jQuery.trim(megaMenuData[i][n][0]) != "") {
                        linkColumn += '
' + jQuery.trim(megaMenuData[i][n][0]) + '';
                    }
                }
                linkColumn += "";
                $(".sw-channel-dropdown .mega-columns", this).prepend(linkColumn);

                // ADD CAPTION
                if(jQuery.trim(megaMenuData[i][0][1]) != "") {
                  var captionColumn = '

' + jQuery.trim(megaMenuData[i][0][1]) + '';
                  $(".sw-channel-dropdown .mega-columns", this).prepend(captionColumn);
                }

                // ADD IMAGE
                var imgColumn = '';
                var imgSrc = jQuery.trim(megaMenuData[i][0][0]);
                var srcSplit = imgSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                    imgColumn = '
';
                }
                $(".sw-channel-dropdown .mega-columns .mega-column.one", this).prepend(imgColumn);

                // ADD BUTTON
                var btnColumn = '' + jQuery.trim(megaMenuData[i][0][3]) + '';
                $(".sw-channel-dropdown .mega-columns .mega-column.one", this).append(btnColumn);

                // ADD CHANNEL HEADER
                if(!$(".mega-column.three h3",this).length) {
                    $(".sw-channel-dropdown .mega-columns .mega-column.three").prepend("
" + channelText + "");
                }
            }
         });

         $(".mega-column.sections").each(function() {
            $("h3",this).last().addClass("channel-header");
            $("h3",this).not(".channel-header").remove();
         });

         $(".mega-column.additional-links ul li").each(function() {
            var thisLink = this;
            if($("a",thisLink).text() == "") {
                $(thisLink).remove();
            }
         });

         $(".mega-column.sections ul li").each(function() {
            var thisLink = this;
            if($("a",thisLink).text() == "") {
                $(thisLink).remove();
            }
         });

         $(".mega-channel-btn").each(function() {
            var thisBtn = this;
            if($(thisBtn).text() == "") {
                $(thisBtn).remove();
            }
         });

         $(".mega-column.additional-links").each(function() {
            var thisCol = this;
            if(!$("ul li",thisCol).length) {
                $(thisCol).remove();
            }
         });

         $(".sw-channel-dropdown").each(function() {
            var thisDropdown = this;
            if($(".mega-column",thisDropdown).length == 1) {
               $("li.mega-columns",thisDropdown).css("width", "228px");
            }
         });
    }

