        });
    }	

    function useChannelForSection(){
        //DOC use channel name for pagelistnavigation header if one is not present
        if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
            $("div.sp.column.one .ui-widget-header").append("
");
        }
    }

    function dynamicContent(){
        // ********** MYSTART ********** //
        //DOC if myview button exists, move into myaccount dropdown
        if($(".sw-mystart-button.pw").length) {
            var myviewBtn = $(".sw-mystart-button.pw").html();
            $("
" + myviewBtn + "").prependTo("#sw-myaccount-list");
            $(".sw-mystart-button.pw").hide();
        }
        if($(".sw-mystart-button.manage").length) {
            var manageBtn = $(".sw-mystart-button.manage").html();
            $("
" + manageBtn + "").prependTo("#sw-myaccount-list");
            $(".sw-mystart-button.manage").hide();
        }

        // ADJUST PASSKEY OVERLAY
        $(document).ajaxComplete(function() {
            if($("#ui-mypasskey-overlay").length && !$("#sw-mystart-mypasskey #ui-mypasskey-overlay").length) {
                $("#sw-mystart-mypasskey").append($("#ui-mypasskey-overlay"));
            }
        });

        //DOC fixes search box not allowing spaces
        $("#sw-mystart-search").unbind();
        $("#sw-search-input").before('Search Input');
        $("#sw-search-button").attr({
            "role": "button",
            "aria-label": "Search Our Site"
        });
        $("#sw-search-button").keydown(function(e) {
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                SWGoToSearchResultsPageswsearchinput();
            }
        });
        //SEARCH MENU
        $("#sw-search-input").attr("tabindex","-1").appendTo("#gb-search-input-cont");
        $("#sw-search-input").before('Search Input');
        $(".gb-search-icon").click(function() {
            if($("#gb-search-input-cont").hasClass("open")) {
                $(".gb-search").removeClass("open");
                $("#gb-search-input-cont").removeClass("open").attr({
                        "aria-hidden": "true"
                });
                $(this).attr({
                        "aria-expanded": "false",
                });
                $(".close-search-btn").attr({
                    "tabindex": "-1",
                    "aria-expanded": "false"
                });
                $("#sw-search-input").attr("tabindex","-1");
            } else {
                $(".gb-search").addClass("open");
                $("#gb-search-input-cont").addClass("open").attr({
                        "aria-hidden": "false"
                });
                $(this).attr({
                        "aria-expanded": "true",
                });
                $(".close-search-btn").attr({
                    "tabindex": "0",
                    "aria-expanded": "true"
                });
                $("#sw-search-input").attr("tabindex","0");
                setTimeout(function() {
                    $("#sw-search-input").focus();
                }, 500);
            }
        });
        $(".gb-search-icon").keydown(function(e) {
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                if($("#gb-search-input-cont").hasClass("open")) {
                    $(".gb-search").removeClass("open");
                    $("#gb-search-input-cont").removeClass("open").attr({
                            "aria-hidden": "true"
                    });
                    $(this).attr({
                            "aria-expanded": "false",
                    });
                    $(".close-search-btn").attr({
                        "tabindex": "-1",
                        "aria-expanded": "false"
                    });
                    $("#sw-search-input").attr("tabindex","-1");
                } else {
                    $(".gb-search").addClass("open");
                    $("#gb-search-input-cont").addClass("open").attr({
                            "aria-hidden": "false"
                    });
                    $(this).attr({
                            "aria-expanded": "true",
                    });
                    $(".close-search-btn").attr({
                        "tabindex": "0",
                        "aria-expanded": "true"
                    });
                    $("#sw-search-input").attr("tabindex","0");
                    setTimeout(function() {
                        $("#sw-search-input").focus();
                    }, 300);
                }
            }
        });
        $(".close-search-btn").click(function() {
            $(".gb-search").removeClass("open");
            $("#gb-search-input-cont").removeClass("open").attr({
                "aria-hidden": "true"
            });
            $(".close-search-btn").attr({
                "tabindex": "-1",
                "aria-expanded": "false"
            });
            $("#sw-search-input").attr("tabindex","-1");
        });
        $(".close-search-btn").keydown(function(e) {
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                $(".gb-search").removeClass("open");
                $("#gb-search-input-cont").removeClass("open").attr({
                    "aria-hidden": "true"
                });
                $(".close-search-btn").attr({
                    "tabindex": "-1",
                    "aria-expanded": "false"
                });
                $("#sw-search-input").attr("tabindex","-1");
                $(".gb-search-icon").focus();
                $(".gb-search-icon").attr("aria-expanded","false");
            } else if(e.keyCode == csKeyCodes.tab) {
                e.preventDefault();
                $("#sw-search-input").focus();
            }
        });
        $("#sw-search-input").focusout(function(e) {
            e.preventDefault();
            $(".close-search-btn").focus();
        });

        //QUICKLINKS DROPDOWN
        $(".gb-quicklinks-btn").click(function() {
            if($("#gb-quicklinks-dropdown").hasClass("open")) {
                $(this).removeClass("open").attr({
                        "aria-expanded": "false",
                });
                $("#gb-quicklinks-dropdown").removeClass("open").attr({
                        "aria-hidden": "true"
                }).slideUp();
                $("#gb-quicklinks-dropdown li a").attr("tabindex","-1");
            } else {
                $(this).addClass("open").attr({
                        "aria-expanded": "true",
                });
                $("#gb-quicklinks-dropdown").addClass("open").attr({
                        "aria-hidden": "false"
                }).slideDown();
                $("#gb-quicklinks-dropdown li a").attr("tabindex","0");
            }
        });
        $(".gb-quicklinks-btn").keydown(function(e) {
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                if($("#gb-quicklinks-dropdown").hasClass("open")) {
                    $(this).removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#gb-quicklinks-dropdown").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#gb-quicklinks-dropdown li a").attr("tabindex","-1");
                } else {
                    $(this).addClass("open").attr({
                            "aria-expanded": "true",
                    });
                    $("#gb-quicklinks-dropdown").addClass("open").attr({
                            "aria-hidden": "false"
                    }).slideDown();
                    $("#gb-quicklinks-dropdown li a").attr("tabindex","0");
                }
            }
        });

        //QUICK LINKS A11Y
        $(".gb-quicklinks-btn").keydown(function(e) {
            switch(e.keyCode) {
                case csKeyCodes.down:
                    e.preventDefault();
                    $("#gb-quicklinks-dropdown ul li:first-child > a").focus();
                break;

                case csKeyCodes.up:
                    e.preventDefault();
                    $("#gb-quicklinks-dropdown ul li:last-child > a").focus();
                break;
            }
        });

        $("#gb-quicklinks-dropdown ul li a").keydown(function(e) {
            var thisParent = $(this).parent().parent().parent().parent();
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        //$(".sw-mystart-button.user-options").focus();
                        //$(this).parent().parent().parent().slideDown();
                        $(".gb-quicklinks-btn").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    $("#gb-quicklinks-dropdown ul li:first-child > a").focus();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $("#gb-quicklinks-dropdown ul li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM

                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $("#gb-quicklinks-dropdown ul li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $("#gb-quicklinks-dropdown ul > a").focus();
                break;

                // CONSUME TAB KEY
                case csKeyCodes.esc:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(".gb-quicklinks-btn").removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#gb-quicklinks-dropdown").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#gb-quicklinks-dropdown li a").attr("tabindex","-1");
                    $(".gb-quicklinks-btn").focus();
                break;

                case csKeyCodes.tab:
                    e.preventDefault();
                    //CLOSE DROPDOWN
                    $(".gb-quicklinks-btn").removeClass("open").attr({
                            "aria-expanded": "false",
                    });
                    $("#gb-quicklinks-dropdown").removeClass("open").attr({
                            "aria-hidden": "true"
                    }).slideUp();
                    $("#gb-quicklinks-dropdown li a").attr("tabindex","-1");
                    $(".gb-search-icon").focus();
                break;

            }
        });

        //DOC change schoollist selector text
        $(".sw-mystart-dropdown.schoollist .selector").text("Schools");

        //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
        //$(".sw-mystart-dropdown").addClass("transition");

        //ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
        $(".sw-mystart-button").addClass("transition");


        // ********** HEADER ********** //
        // ADD LOGO
        var logoSrc = jQuery.trim("/cms/lib/AR02203631/Centricity/Template/GlobalAssets/images///logos/LR-finaldistrictlogo.png");
        var srcSplit = logoSrc.split("/");
        var srcSplitLen = srcSplit.length;
        if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
            $("#gb-logo, #footer-logo").append('').removeClass("hidden");
        } else {
            $("#gb-logo, #footer-logo").append('').removeClass("hidden");
        }

        //SCHOOL NAME LINE 3 - ONLY FOR SCHOOL SITES
        if('ar02203631' == 'ar02203631' || 'Little Rock School District' == '') {
            $("#gb-sitename h3").remove();
        }

        // ********** CHANNEL BAR ********** //
        //DOC add first class to first channel item
        $("li.sw-channel-item").first().addClass("first");

        //DOC add last class to last channel item
        $("li.sw-channel-item").last().addClass("last");

        //DOC add aria to channels and dropdown items
        $(".sw-channel-item").attr("aria-haspopup","true");
        $(".sw-channel-dropdown li").attr("aria-haspopup","false");

        //ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
        $("ul.sw-channel-list li.sw-channel-item").addClass("transition");

        // ********** BODY ********** //
        //DOC rename first breadcrumb
        $("li.ui-breadcrumb-first > a > span").text("Home");

        //DOC replace more-link text
        $("a.more-link span").text("More Â»");

        //DOC replace ui-return text for headlines
        $(".headlines.detail .ui-return span").text("Â« Return to Headlines");

        //DOC auto-focus username signin field
        $("#swsignin-txt-username").focus();

        //DOC center signed out message and signin button
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //DOC add signed out breadcrumb
            $(".ui-breadcrumbs").append("
Signed Out");
        }

        //DOC add sign in breadcrumb
        if($("#swlogin").length){
            $(".ui-breadcrumbs").append("
Sign In");
        }

        //REGISTER BREADCRUMB
        if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
            $(".ui-breadcrumbs").append("
Register");
        }

        //SEARCH RESULTS BREADCRUMB
        if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
            $("#sw-content-layout-wrapper").children().wrapAll('

');
            $(".ui-breadcrumbs").append("
Search Results");
        }

        //MOD EVENTS
