    function MyStartDropdownActions(params) {
        var KeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

        var dropdownParent = params.dropdownParent;
        var dropdownSelector = params.dropdownSelector;
        var dropdown = params.dropdown;
        var dropdownList = params.dropdownList;

        // MYSTART DROPDOWN SELECTOR CLICK EVENT
        $(document).on("click", dropdownParent + " " + dropdownSelector, function(e) {
            e.preventDefault();

            if($(this).parent().hasClass("open")){
                $("+ " + dropdownList + " a").attr("tabindex", "-1");
                $(this).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            } else {
                $(this).attr({"aria-expanded": "true"}).parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
            }
        });

        // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
        $(document).on("keydown", dropdownParent + " " + dropdownSelector, function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case KeyCodes.enter:
                case KeyCodes.space:
                    e.preventDefault();

                    // IF THE DROPDOWN IS OPEN, CLOSE IT
                    if($(dropdownParent).hasClass("open")){
                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    } else {
                        $(this).attr({"aria-expanded": "true"}).parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                            $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                        });
                    }

                break;

                // CONSUME TAB KEY
                case KeyCodes.tab:
                    $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open");//.find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case KeyCodes.down:
                case KeyCodes.right:
                    e.preventDefault();

                    $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                    $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();

                break;
            }
        });

        // MYSTART DROPDOWN LINK KEYDOWN EVENTS
        $(document).on("keydown", dropdownParent + " " + dropdownList + " li a", function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case KeyCodes.left:
                case KeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case KeyCodes.right:
                case KeyCodes.down:
                    e.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME TAB KEY
                case KeyCodes.tab:
                    e.preventDefault();

                    if(e.shiftKey) {
                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    } else {
                        // IS LAST ITEM
                        /*if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }*/

                        //$(dropdownParent).next().find(dropdownSelector).focus();
                    }
                break;

                // CONSUME HOME KEY
                case KeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                break;

                // CONSUME END KEY
                case KeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                break;

                // CONSUME END KEY
                case KeyCodes.esc:
                    e.preventDefault();

                    // FOCUS DROPDOWN BUTTON
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownParent).find(dropdownSelector).focus();
                    $(this).closest(dropdownParent).removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                break;
            }
        });

        $(dropdownParent).mouseleave(function() {
                  $("+ " + dropdownList + " a", this).attr("tabindex", "-1");
                  $(dropdownSelector, this).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
              }).focusout(function() {
                  var thisDropdown = this;
                  setTimeout(function () {
                      if(!$(thisDropdown).find(":focus").length) {
                          $(dropdownSelector, thisDropdown).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                      }
                  }, 500);
              });

        $(".sw-mystart-dropdown.schoollist .sw-dropdown li a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                    e.preventDefault();
                    $(".sw-mystart-dropdown.schoollist .sw-dropdown").slideUp().attr("aria-hidden","true"); 
                    $(".sw-mystart-dropdown.translate .selector").focus();
                break;
            }
        });

        $(".sw-mystart-dropdown.translate .sw-dropdown li a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                    e.preventDefault();
                    $(".sw-mystart-dropdown.translate .sw-dropdown").slideUp().attr("aria-hidden","true"); 
                    $(".sw-mystart-dropdown.cs-user-options .selector").focus();
                break;
            }
        });

        $(".sw-mystart-dropdown.cs-user-options .sw-dropdown li a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                    e.preventDefault();
                    $(".sw-mystart-dropdown.cs-user-options .sw-dropdown").slideUp().attr("aria-hidden","true"); 
                    $(".header-icon.virtual-backpack").focus();
                break;
            }
        });
    }

//loads the IFrame Player API code asynchronously.

      var tag = document.createElement('script');

      tag.src = "http://www.youtube.com/iframe_api";
      var firstScriptTag = document.getElementsByTagName('script')[0];
      firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);


      //creates an  (and YouTube player)
    //after the API code downloads.
    var player;
    var windowWidth;
    var windowHeight;
    var alertHeight;

        function onYouTubeIframeAPIReady() {
            if('MMR' == 'Video') {
                $("#hp-slideshow").addClass("mediaType-video");
                $('
').prependTo("#hp-slideshow");
                $("#hp-slideshow #sw-content-container10").remove();
                    windowWidth = $(window).width();
                    windowHeight = $(window).height();
                    player = new YT.Player('player', {
                        height: windowHeight,
                        width: windowWidth,
                        videoId: '0eELWf6plRY',
                        playerVars: { 
                            'modestbranding': 1,
                                'controls': 0, 
                                'autoplay': 1,
                                'rel' : 0,
                                'loop': 1,
                                'wmode': 'opaque',
                                'showinfo': 0,
                                'playlist': '0eELWf6plRY'
                        },
                        events: {
                            'onReady': onPlayerReady,
                            'onStateChange': onPlayerStateChange
                        }
                    });
            }

            $("#hp-video-player iframe").attr("tabindex","-1");
        }
    //the API will call this function when the video player is ready.
    function onPlayerReady(event) {
        if($(window).width() > 1023) {
            $(".cs-video-btn").attr("aria-label","Pause Video");
            $(".cs-video-btn").addClass("pause");
        }
        csVideoResize();
        $(window).bind("resize", function(){ 
            csVideoResize(); 
        });
        player.mute();
        $(".cs-video-btn").click(function() {
            $(this).toggleClass("pause");
            $(this).toggleClass("play");
            pausePlayVideo();
        });
    }

    //The API calls this function when the players state changes.
    //The function indicates that when playing a video (state=1),
    //the player should play for six seconds and then stop.
    var done = false;
    function onPlayerStateChange(event) {
        if($(window).width() > 1023) {
            //pausePlayVideo();
        }
    }
    function pausePlayVideo() {
        if($(".cs-video-btn").hasClass("pause")) {
            $(".cs-video-btn").attr("aria-label","Pause Video");
            $(".cs-video-btn").html("î¤ƒ");
            player.playVideo();
        } else {
            $(".cs-video-btn").attr("aria-label","Play Video");
            $(".cs-video-btn").html("î¤„");
            player.pauseVideo();
        }
    }
    function stopVideo() {
        player.pauseVideo();
    }
    function csVideoResize() {
        if($("#player").length) {
            var screenWidth = $(window).width();
            var screenHeight = $(window).height();
            var vidWidth = screenWidth, vidHeight = screenHeight;

            // GET IMAGE RATIO
            var initW = 853.5, initH = 480;
            var ratio = initH / initW;
            vidHeight = vidWidth * ratio;
            var vidPlayerHeight = screenHeight - 138;

            // APPLY THE DIMENSIONS
            $("#player, #hp-slideshow").attr("tabindex","-1").css({"height": vidHeight + "px" , "width": screenWidth + "px"});

            if(screenWidth > 1023) {
                $("#hp-slideshow").css({"height": vidPlayerHeight + "px" , "width": screenWidth + "px"});
            }
        }
    }

