        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/CA01902661/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
            var siteNameOne = jQuery.trim('PITTSBURG');
            var siteNameTwo = jQuery.trim('Unified School District');
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Pittsburg Unified School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
            }

            //ADD TAGLINE
            var tagLine = jQuery.trim('Every Scholar, Every Day. They Deserve Nothing Less Than Our Best.');
            if(tagLine != ""){
            	//$("#gb-header").append('

' + tagLine + '');
                $('

' + tagLine + '').appendTo("#gb-logo-outer");
            };
        },
        "ChannelBar": function() {
            //USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //ADD FIRST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").first().addClass("first");
            //ADD LAST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").last().addClass("last");

            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            //ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
            $("ul.sw-channel-list li.sw-channel-item").addClass("transition");

            //REPLACE HOME CHANNEL TEXT WITH ICON AND INSERT SITEMAP ICON
            var homeOn = 'Yes';
            var sitemapLink = $(".footer-link.site-map a").attr("href");
            var siteMapChannel = '
';
            if(homeOn == "Yes"){
            	$("ul.sw-channel-list li.sw-channel-item").first().addClass("home");
            	$("ul.sw-channel-list li.sw-channel-item.first a").html('').attr("aria-label","home");
                $(siteMapChannel).insertAfter("ul.sw-channel-list li.sw-channel-item.home");
            } else {
            	$("ul.sw-channel-list").prepend(siteMapChannel);
            }

            //ADD CLASS TO CALENDAR CHANNEL
            var calendarOn = 'No';
            if(calendarOn == "Yes"){
            	$("ul.sw-channel-list li.sw-channel-item").last().addClass("calendar");
            }
        },
         "StickyChannelBar": function() {
            var channelHeight = $("#gb-channel-list-outer").height();

            if ($(window).scrollTop() <= channelHeight) {

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    $(".wcm-controls").css("margin-top", "0px");
                }
            } else {

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", channelHeight + "px");
                    }
                }
            }
        },
        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();



           //RENAME FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");



            //AUTO-FOCUS UERSNAME SIGIN FIELD
            $("#swsignin-txt-username").focus();

            //CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
                if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                    $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                    //ADD SIGNED OUT BREADCRUMB
                    $(".ui-breadcrumbs").append("
Signed Out");
                }

            //ADD READ FULL STORY BUTTON TO EACH HEADLINE
            $(".ui-widget.app.headlines").each(function(){
                $(".ui-article", this).each(function(){
                    var headlineURL = $("h1 a", this).attr("href");
                    $('Learn More').insertAfter($(".ui-article-description", this));
                });
