        "Footer": function() {

        	var template = this;

        	// SET GOOGLE MAP IFRAME SRC
            var googleMap = "//maps.google.com/maps?q=" + encodeURIComponent("2000 Railroad Avenue+Pittsburg+CA+94565") + "&output=embed&z=12";
            $("#gb-footer-map").html('');

            // GOOGLE MAP CLICK
            $(document).on("click keydown", "#gb-footer-map", function(e) {
                if(template.AllyClick(e)) {
                    e.preventDefault();
                    var mapURL = encodeURIComponent("2000 Railroad Avenue Pittsburg CA 94565");
                    window.location = "//www.google.com/maps/place/" + mapURL;
                }
            });

        	//ADD SITENAME
            var siteNameOne = jQuery.trim('PITTSBURG');
            var siteNameTwo = jQuery.trim('Unified School District');
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Pittsburg Unified School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-footer-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-footer-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-footer-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-footer-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
            }

            //PHONE AND FAX NUMBERS
            if("925.473.2300" != "") {
                $(".gb-footer-contact.phone").removeClass("hidden");
            }
            if("925.439.1650" != "") {
                $(".gb-footer-contact.fax").removeClass("hidden");
            }

            //ADD FOOTER LINKS TO GB.FOOTER.TWO
            var footerLinks= "";
            var footerLinkDeets = [
            	["one","Non-discrimination Policy","https://www.pittsburg.k12.ca.us/Page/1172","_blank"],
