                ["three","Contact Webmaster","https://www.pittsburg.k12.ca.us/Domain/1442","_blank"],
                ["four","","#","_blank"],
                ["five","","#","_blank"]
            ];

            footerLinks += '
District Home';


            $(".sw-mystart-button.signin a span").text("Website Login");
            footerLinks += '
